03508 
03509     static class SetPropertyUtility
03510     {
03511         public static bool SetColor(ref Color currentValue, Color newValue)
03512         {
03513             if (currentValue.r == newValue.r && currentValue.g == newValue.g && currentValue.b == newValue.b && currentValue.a == newValue.a)
03514                 return false;
03515 
03516             currentValue = newValue;
03517             return true;
03518         }
03519 
03520         public static bool SetEquatableStruct<T>(ref T currentValue, T newValue) where T : IEquatable<T>
03521         {
03522             if (currentValue.Equals(newValue))
03523                 return false;
03524 
03525             currentValue = newValue;
03526             return true;
03527         }
03528 
03529         public static bool SetStruct<T>(ref T currentValue, T newValue) where T : struct
03530         {
03531             if (currentValue.Equals(newValue))
03532                 return false;
03533 
03534             currentValue = newValue;
03535             return true;
03536         }
03537 
03538         public static bool SetClass<T>(ref T currentValue, T newValue) where T : class
03539         {
03540             if ((currentValue == null && newValue == null) || (currentValue != null && currentValue.Equals(newValue)))
03541                 return false;
03542 
03543             currentValue = newValue;
03544             return true;
03545         }
03546     }
