00052 
00053             // Limit the mesh to less than 65535 vertices which is the limit for Unity's Mesh.
00054             size = Mathf.Min(size, 16383);
00055 
00056             int sizeX4 = size * 4;
00057             int sizeX6 = size * 6;
00058 
