00072             int index_X6 = 0;
00073             int index_X4 = 0;
00074             while (index_X4 / 4 < size)
00075             {
00076                 for (int i = 0; i < 4; i++)
00077                 {
00078                     this.vertices[index_X4 + i] = Vector3.zero;
00079                     this.uvs0[index_X4 + i] = Vector2.zero;
00080                     this.uvs2[index_X4 + i] = Vector2.zero;
