00083                     this.normals[index_X4 + i] = s_DefaultNormal;
00084                     this.tangents[index_X4 + i] = s_DefaultTangent;
00085                 }
00086 
00087                 this.triangles[index_X6 + 0] = index_X4 + 0;
00088                 this.triangles[index_X6 + 1] = index_X4 + 1;
00089                 this.triangles[index_X6 + 2] = index_X4 + 2;
00090                 this.triangles[index_X6 + 3] = index_X4 + 2;
00091                 this.triangles[index_X6 + 4] = index_X4 + 3;
00092                 this.triangles[index_X6 + 5] = index_X4 + 0;
00093 
00094                 index_X4 += 4;
00095                 index_X6 += 6;
00096             }
00097 
00098             // Pre-assign base vertex attributes.
00099             this.mesh.vertices = this.vertices;
00100             this.mesh.normals = this.normals;
00101             this.mesh.tangents = this.tangents;
00102             this.mesh.triangles = this.triangles;
