00497         public void SortGeometry (VertexSortingOrder order)
00498         {
00499             switch (order)
00500             {
00501                 case VertexSortingOrder.Normal:
00502                     // Do nothing 
00503                     break;
00504                 case VertexSortingOrder.Reverse:
00505                     int size = vertexCount / 4;
00506                     for (int i = 0; i < size; i++)
00507                     {
00508                         int src = i * 4;
00509                         int dst = (size - i - 1) * 4;
00510 
00511                         if (src < dst)
00512                             SwapVertexData(src, dst);
00513 
00514                     }
00515                     break;
00516                 //case VertexSortingOrder.Depth:
00517                 //    break;
00518 
00519             }
00520         }
