00527         public void SortGeometry(IList<int> sortingOrder)
00528         {
00529             // Make sure the sorting order array is not larger than the vertices array.
00530             int indexCount = sortingOrder.Count;
00531 
00532             if (indexCount * 4 > vertices.Length) return;
00533 
00534             int src_index;
00535 
00536             for (int dst_index = 0; dst_index < indexCount; dst_index++)
00537             {
00538                 src_index = sortingOrder[dst_index];
00539 
00540                 while (src_index < dst_index)
00541                 {
00542                     src_index = sortingOrder[src_index];
00543                 }
00544 
00545                 // Swap items
00546                 if (src_index != dst_index)
00547                     SwapVertexData(src_index * 4, dst_index * 4);
00548 
00549                 //Debug.Log("Swap element [" + dst_index + "] with [" + src_index + "]. Vertex[" + dst_index + "] is " + vertices[dst_index * 4].z);
00550             }
00551         }
