00568             vertices[src_Index + 0] = vertex;
00569 
00570             vertex = vertices[dst_Index + 1];
00571             vertices[dst_Index + 1] = vertices[src_Index + 1];
00572             vertices[src_Index + 1] = vertex;
00573 
00574             vertex = vertices[dst_Index + 2];
00575             vertices[dst_Index + 2] = vertices[src_Index + 2];
00576             vertices[src_Index + 2] = vertex;
00577 
00578             vertex = vertices[dst_Index + 3];
00579             vertices[dst_Index + 3] = vertices[src_Index + 3];
00580             vertices[src_Index + 3] = vertex;
00581 
00582 
00583             //Swap UVs0
00584             Vector2 uvs;
00585             uvs = uvs0[dst_Index + 0];
00586             uvs0[dst_Index + 0] = uvs0[src_Index + 0];
00587             uvs0[src_Index + 0] = uvs;
00588 
00589             uvs = uvs0[dst_Index + 1];
00590             uvs0[dst_Index + 1] = uvs0[src_Index + 1];
00591             uvs0[src_Index + 1] = uvs;
00592 
00593             uvs = uvs0[dst_Index + 2];
00594             uvs0[dst_Index + 2] = uvs0[src_Index + 2];
00595             uvs0[src_Index + 2] = uvs;
00596 
00597             uvs = uvs0[dst_Index + 3];
00598             uvs0[dst_Index + 3] = uvs0[src_Index + 3];
00599             uvs0[src_Index + 3] = uvs;
00600 
00601             // Swap UVs2
