00084         [Test, TestCaseSource("TestCases_Parsing_TextInfo_WordWrapDisabled")]
00085         public void Parsing_TextInfo_WordWrapDisabled(int sourceTextIndex, int characterCount, int spaceCount, int wordCount, int lineCount)
00086         {
00087             m_TextComponent.text = testStrings[sourceTextIndex];
00088             m_TextComponent.enableWordWrapping = false;
00089             m_TextComponent.alignment = TextAlignmentOptions.TopLeft;
00090 
00091             // Size the RectTransform
00092             m_TextComponent.rectTransform.sizeDelta = new Vector2(50, 5);
00093 
00094             // Force text generation to populate the TextInfo data structure.
00095             m_TextComponent.ForceMeshUpdate();
00096 
00097             Assert.AreEqual(m_TextComponent.textInfo.characterCount, characterCount);
00098             Assert.AreEqual(m_TextComponent.textInfo.spaceCount, spaceCount);
00099             Assert.AreEqual(m_TextComponent.textInfo.wordCount, wordCount);
00100             Assert.AreEqual(m_TextComponent.textInfo.lineCount, lineCount);
00101         }
