00395         void DoMaskSubgroup()
00396         {
00397             DoVector("_MaskCoord", "Mask Bounds", s_XywhVectorLabels);
00398             if (Selection.activeGameObject != null)
00399             {
00400                 Renderer renderer = Selection.activeGameObject.GetComponent<Renderer>();
00401                 if (renderer != null)
00402                 {
00403                     Rect rect = EditorGUILayout.GetControlRect();
00404                     rect.x += EditorGUIUtility.labelWidth;
00405                     rect.width -= EditorGUIUtility.labelWidth;
00406                     if (GUI.Button(rect, "Match Renderer Bounds"))
00407                     {
00408                         FindProperty("_MaskCoord", m_Properties).vectorValue = new Vector4(
00409                             0,
00410                             0,
00411                             Mathf.Round(renderer.bounds.extents.x * 1000) / 1000,
00412                             Mathf.Round(renderer.bounds.extents.y * 1000) / 1000
00413                         );
00414                     }
00415                 }
00416             }
00417 
00418             if (s_MaskFeature.State == 1)
00419             {
00420                 DoFloat("_MaskSoftnessX", "Softness X");
00421                 DoFloat("_MaskSoftnessY", "Softness Y");
00422             }
00423         }
