00108 
00109             m_List.drawElementCallback = (rect, index, isActive, isFocused) =>
00110             {
00111                 var element = m_List.serializedProperty.GetArrayElementAtIndex(index);
00112                 rect.y += 2;
00113                 EditorGUI.PropertyField(new Rect(rect.x, rect.y, rect.width, EditorGUIUtility.singleLineHeight), element, GUIContent.none);
00114             };
00115 
00116             m_List.drawHeaderCallback = rect =>
00117             {
00118                 EditorGUI.LabelField(rect, Styles.fallbackFontAssetsListLabel);
00119             };
00120 
00121             m_PropMatchMaterialPreset = serializedObject.FindProperty("m_matchMaterialPreset");
00122 
00123             m_PropWordWrapping = serializedObject.FindProperty("m_enableWordWrapping");
00124             m_PropKerning = serializedObject.FindProperty("m_enableKerning");
