00343         void DisplayGlyphPageNavigation(int arraySize, int itemsPerPage)
00344         {
00345             Rect pagePos = EditorGUILayout.GetControlRect(false, 20);
00346             pagePos.width /= 3;
00347 
00348             int shiftMultiplier = Event.current.shift ? 10 : 1; // Page + Shift goes 10 page forward
00349 
00350             // Previous Page
00351             GUI.enabled = m_page > 0;
00352 
00353             if (GUI.Button(pagePos, "Previous Page"))
00354             {
00355                 m_page -= 1 * shiftMultiplier;
00356                 //m_isNewPage = true;
00357             }
00358 
00359             // Page Counter
00360             GUI.enabled = true;
00361             pagePos.x += pagePos.width;
00362             int totalPages = (int)(arraySize / (float)itemsPerPage + 0.999f);
00363             GUI.Label(pagePos, "Page " + (m_page + 1) + " / " + totalPages, TMP_UIStyleManager.centeredLabel);
00364 
00365             // Next Page
00366             pagePos.x += pagePos.width;
00367             GUI.enabled = itemsPerPage * (m_page + 1) < arraySize;
00368 
00369             if (GUI.Button(pagePos, "Next Page"))
00370             {
00371                 m_page += 1 * shiftMultiplier;
00372                 //m_isNewPage = true;
00373             }
00374 
00375             // Clamp page range
00376             m_page = Mathf.Clamp(m_page, 0, arraySize / itemsPerPage);
00377 
00378             GUI.enabled = true;
00379         }
