00387         void UpdateGlobalProperty(string property, float value)
00388         {
00389             int arraySize = m_spriteInfoList_prop.arraySize;
00390 
00391             for (int i = 0; i < arraySize; i++)
00392             {
00393                 SerializedProperty spriteInfo = m_spriteInfoList_prop.GetArrayElementAtIndex(i);
00394                 spriteInfo.FindPropertyRelative(property).floatValue = value;
00395             }
00396 
00397             GUI.changed = false;
00398         }
