00400         // Check if any of the Style elements were clicked on.
00401         private bool DoSelectionCheck(Rect selectionArea)
00402         {
00403             Event currentEvent = Event.current;
00404 
00405             switch (currentEvent.type)
00406             {
00407                 case EventType.MouseDown:
00408                     if (selectionArea.Contains(currentEvent.mousePosition) && currentEvent.button == 0)
00409                     {
00410                         currentEvent.Use();
00411                         return true;
00412                     }
00413                     break;
00414             }
00415 
00416             return false;
00417         }
