00425         void SwapSpriteElement(int selectedIndex, int newIndex)
00426         {
00427             m_spriteInfoList_prop.MoveArrayElement(selectedIndex, newIndex);
00428             m_spriteInfoList_prop.GetArrayElementAtIndex(selectedIndex).FindPropertyRelative("id").intValue = selectedIndex;
00429             m_spriteInfoList_prop.GetArrayElementAtIndex(newIndex).FindPropertyRelative("id").intValue = newIndex;
00430             m_selectedElement = newIndex;
00431             m_isSearchDirty = true;
00432         }
00433 
00439         void MoveSpriteElement(int selectedIndex, int newIndex)
00440         {
00441             m_spriteInfoList_prop.MoveArrayElement(selectedIndex, newIndex);
00442 
00443             // Reorder Sprite Element Index
00444             for (int i = 0; i < m_spriteInfoList_prop.arraySize; i++)
00445                 m_spriteInfoList_prop.GetArrayElementAtIndex(i).FindPropertyRelative("id").intValue = i;
00446 
00447             m_selectedElement = newIndex;
00448             m_isSearchDirty = true;
00449         }
