00457         void CopySerializedProperty(SerializedProperty source, ref SerializedProperty target)
00458         {
00459             target.FindPropertyRelative("name").stringValue = source.FindPropertyRelative("name").stringValue;
00460             target.FindPropertyRelative("hashCode").intValue = source.FindPropertyRelative("hashCode").intValue;
00461             target.FindPropertyRelative("x").floatValue = source.FindPropertyRelative("x").floatValue;
00462             target.FindPropertyRelative("y").floatValue = source.FindPropertyRelative("y").floatValue;
00463             target.FindPropertyRelative("width").floatValue = source.FindPropertyRelative("width").floatValue;
00464             target.FindPropertyRelative("height").floatValue = source.FindPropertyRelative("height").floatValue;
00465             target.FindPropertyRelative("xOffset").floatValue = source.FindPropertyRelative("xOffset").floatValue;
00466             target.FindPropertyRelative("yOffset").floatValue = source.FindPropertyRelative("yOffset").floatValue;
00467             target.FindPropertyRelative("xAdvance").floatValue = source.FindPropertyRelative("xAdvance").floatValue;
00468             target.FindPropertyRelative("scale").floatValue = source.FindPropertyRelative("scale").floatValue;
