00478         void SearchGlyphTable(string searchPattern, ref List<int> searchResults)
00479         {
00480             if (searchResults == null) searchResults = new List<int>();
00481             searchResults.Clear();
00482 
00483             int arraySize = m_spriteInfoList_prop.arraySize;
00484 
00485             for (int i = 0; i < arraySize; i++)
00486             {
00487                 SerializedProperty sourceSprite = m_spriteInfoList_prop.GetArrayElementAtIndex(i);
00488 
00489                 // Check for potential match against decimal id
00490                 int id = sourceSprite.FindPropertyRelative("id").intValue;
00491                 if (id.ToString().Contains(searchPattern))
00492                     searchResults.Add(i);
00493 
00494                 // Check for potential match against name
00495                 string name = sourceSprite.FindPropertyRelative("name").stringValue.ToLower(System.Globalization.CultureInfo.InvariantCulture).Trim();
00496                 if (name.Contains(searchPattern))
00497                     searchResults.Add(i);
00498             }
00499         }
