00237 
00238             if (GUI.Button(pagePos, "Next"))
00239                 m_Page += 1 * shiftMultiplier;
00240 
00241             // Clamp page range
00242             m_Page = Mathf.Clamp(m_Page, 0, arraySize / itemsPerPage);
00243 
00244 
00245             if (serializedObject.ApplyModifiedProperties())
00246                 TMPro_EventManager.ON_TEXT_STYLE_PROPERTY_CHANGED(true);
00247 
00248             // Clear selection if mouse event was not consumed. 
00249             GUI.enabled = true;
00250             if (currentEvent.type == EventType.MouseDown && currentEvent.button == 0)
00251                 m_SelectedElement = -1;
00252             
