00257         // Check if any of the Style elements were clicked on.
00258         static bool DoSelectionCheck(Rect selectionArea)
00259         {
00260             Event currentEvent = Event.current;
00261 
00262             switch (currentEvent.type)
00263             {
00264                 case EventType.MouseDown:
00265                     if (selectionArea.Contains(currentEvent.mousePosition) && currentEvent.button == 0)
00266                     {
00267                         currentEvent.Use();
00268                         return true;
00269                     }
00270                     break;
00271             }
00272 
00273             return false;
00274         }
