00427         public float pixelsPerUnit
00428         {
00429             get
00430             {
00431                 var localCanvas = canvas;
00432                 if (!localCanvas)
00433                     return 1;
00434                 // For dynamic fonts, ensure we use one pixel per pixel on the screen.
00435                 if (!font)
00436                     return localCanvas.scaleFactor;
00437                 // For non-dynamic fonts, calculate pixels per unit based on specified font size relative to font object's own font size.
00438                 if (m_currentFontAsset == null || m_currentFontAsset.fontInfo.PointSize <= 0 || m_fontSize <= 0)
00439                     return 1;
00440                 return m_fontSize / m_currentFontAsset.fontInfo.PointSize;
00441             }
00442         }
00443 
