00681         public TMP_Text linkedTextComponent
00682         {
00683             get { return m_linkedTextComponent; }
00684 
00685             set
00686             {
00687                 if (m_linkedTextComponent != value)
00688                 {
00689                     // Release previously linked text component.
00690                     if (m_linkedTextComponent != null)
00691                     {
00692                         m_linkedTextComponent.overflowMode = TextOverflowModes.Overflow;
00693                         m_linkedTextComponent.linkedTextComponent = null;
00694                         m_linkedTextComponent.isLinkedTextComponent = false;
00695                     }
00696 
00697                     m_linkedTextComponent = value;
00698 
00699                     if (m_linkedTextComponent != null)
00700                         m_linkedTextComponent.isLinkedTextComponent = true;
00701                 }
00702 
00703                 m_havePropertiesChanged = true;
00704                 m_isCalculateSizeRequired = true;
00705                 SetVerticesDirty();
00706                 SetLayoutDirty();
00707             }
00708         }
