01575         internal void SetTextInternal(string text)
01576         {
01577             m_text = text;
01578             m_renderMode = TextRenderFlags.DontRender;
01579             m_isInputParsingRequired = true;
01580             ForceMeshUpdate();
01581             m_renderMode = TextRenderFlags.Render;
01582         }
