01680         protected void ParseInputText()
01681         {
01682             //Debug.Log("Re-parsing Text.");
01684 
01685             m_isInputParsingRequired = false;
01686 
01687             switch (m_inputSource)
01688             {
01689                 case TextInputSources.String:
01690                 case TextInputSources.Text:
01691                     StringToCharArray(m_text, ref m_char_buffer);
01692                     break;
01693                 case TextInputSources.SetText:
01694                     SetTextArrayToCharArray(m_input_CharArray, ref m_char_buffer);
01695                     break;
01696                 case TextInputSources.SetCharArray:
01697                     break;
01698             }
01699 
01700             SetArraySizes(m_char_buffer);
01702         }
