01726 
01727             StringToCharArray(text, ref m_char_buffer);
01728 
01729             #if UNITY_EDITOR
01730             // Set the text in the Text Input Box in the Unity Editor only.
01731             // TODO: Could revise to convert to string literal
01732             if (syncTextInputBox)
01733                 m_text = text;
01734             #endif
01735 
01736             m_isInputParsingRequired = true;
01737             m_havePropertiesChanged = true;
01738             m_isCalculateSizeRequired = true;
