01779         public void SetText(string text, float arg0, float arg1, float arg2)
01780         {
01781             // Early out if nothing has been changed from previous invocation.
01782             //if (text == old_text && arg0 == old_arg0 && arg1 == old_arg1 && arg2 == old_arg2)
01783             //{
01784             //    return;
01785             //}
01786 
01787             //old_text = text;
01788             //old_arg1 = 255;
01789             //old_arg2 = 255;
01790 
01791             int decimalPrecision = 0;
01792             int index = 0;
01793 
01794             for (int i = 0; i < text.Length; i++)
01795             {
01796                 char c = text[i];
01797 
01798                 if (c == 123) // '{'
01799                 {
01800                     // Check if user is requesting some decimal precision. Format is {0:2}
01801                     if (text[i + 2] == 58) // ':'
01802                     {
01803                         decimalPrecision = text[i + 3] - 48;
01804                     }
01805 
01806                     switch (text[i + 1] - 48)
01807                     {
01808                         case 0: // 1st Arg
01809                             //old_arg0 = arg0;
01810                             AddFloatToCharArray(arg0, ref index, decimalPrecision);
01811                             break;
01812                         case 1: // 2nd Arg
01813                             //old_arg1 = arg1;
01814                             AddFloatToCharArray(arg1, ref index, decimalPrecision);
01815                             break;
01816                         case 2: // 3rd Arg
01817                             //old_arg2 = arg2;
01818                             AddFloatToCharArray(arg2, ref index, decimalPrecision);
01819                             break;
01820                     }
01821 
01822                     if (text[i + 2] == 58)
01823                         i += 4;
01824                     else
01825                         i += 2;
01826 
01827                     continue;
01828                 }
01829                 m_input_CharArray[index] = c;
01830                 index += 1;
01831             }
01832 
01833             m_input_CharArray[index] = (char)0;
01834             m_charArray_Length = index; // Set the length to where this '0' termination is.
01835 
01836             #if UNITY_EDITOR
01837             // Create new string to be displayed in the Input Text Box of the Editor Panel.
01838             m_text = new string(m_input_CharArray, 0, index);
01839             #endif
01840 
01841             m_inputSource = TextInputSources.SetText;
01842             m_isInputParsingRequired = true;
01843             m_havePropertiesChanged = true;
01844             m_isCalculateSizeRequired = true;
01845 
01846             SetVerticesDirty();
01847             SetLayoutDirty();
01848         }
