01882         public void SetCharArray(char[] sourceText)
01883         {
01884             // Initialize internal character buffer if necessary
01885             if (m_char_buffer == null) m_char_buffer = new int[8];
01886 
01887             #if UNITY_EDITOR
01888             // Create new string to be displayed in the Input Text Box of the Editor Panel.
01889             if (sourceText == null || sourceText.Length == 0)
01890                 m_text = string.Empty;
01891             else
01892                 m_text = new string(sourceText);
01893             #endif
01894 
01895             // Clear the Style stack.
01896             m_styleStack.Clear();
01897 
01898             int writeIndex = 0;
01899 
01900             for (int i = 0; sourceText != null && i < sourceText.Length; i++)
01901             {
01902                 if (sourceText[i] == 92 && i < sourceText.Length - 1)
01903                 {
01904                     switch ((int)sourceText[i + 1])
01905                     {
01906                         case 110: // \n LineFeed
01907                             if (writeIndex == m_char_buffer.Length) ResizeInternalArray(ref m_char_buffer);
01908 
01909                             m_char_buffer[writeIndex] = (char)10;
01910                             i += 1;
01911                             writeIndex += 1;
01912                             continue;
01913                         case 114: // \r LineFeed
01914                             if (writeIndex == m_char_buffer.Length) ResizeInternalArray(ref m_char_buffer);
01915 
01916                             m_char_buffer[writeIndex] = (char)13;
01917                             i += 1;
01918                             writeIndex += 1;
01919                             continue;
01920                         case 116: // \t Tab
01921                             if (writeIndex == m_char_buffer.Length) ResizeInternalArray(ref m_char_buffer);
01922 
01923                             m_char_buffer[writeIndex] = (char)9;
01924                             i += 1;
01925                             writeIndex += 1;
01926                             continue;
01927                     }
01928                 }
01929 
01930                 // Handle inline replacement of <stlye> and <br> tags.
01931                 if (sourceText[i] == 60)
01932                 {
01933                     if (IsTagName(ref sourceText, "<BR>", i))
01934                     {
01935                         if (writeIndex == m_char_buffer.Length) ResizeInternalArray(ref m_char_buffer);
01936 
01937                         m_char_buffer[writeIndex] = 10; ;
01938                         writeIndex += 1;
01939                         i += 3;
01940 
01941                         continue;
01942                     }
01943                     else if (IsTagName(ref sourceText, "<STYLE=", i))
01944                     {
01945                         int srcOffset = 0;
01946                         if (ReplaceOpeningStyleTag(ref sourceText, i, out srcOffset, ref m_char_buffer, ref writeIndex))
01947                         {
01948                             i = srcOffset;
01949                             continue;
01950                         }
01951                     }
01952                     else if (IsTagName(ref sourceText, "</STYLE>", i))
01953                     {
01954                         ReplaceClosingStyleTag(ref sourceText, i, ref m_char_buffer, ref writeIndex);
01955 
01956                         // Strip </style> even if style is invalid.
01957                         i += 7;
01958                         continue;
01959                     }
01960                 }
01961 
01962                 if (writeIndex == m_char_buffer.Length) ResizeInternalArray(ref m_char_buffer);
01963 
01964                 m_char_buffer[writeIndex] = sourceText[i];
01965                 writeIndex += 1;
01966             }
01967 
01968             if (writeIndex == m_char_buffer.Length) ResizeInternalArray(ref m_char_buffer);
01969 
01970             m_char_buffer[writeIndex] = (char)0;
01971 
01972             m_inputSource = TextInputSources.SetCharArray;
01973             m_isInputParsingRequired = true;
01974             m_havePropertiesChanged = true;
01975             m_isCalculateSizeRequired = true;
01976 
01977             SetVerticesDirty();
01978             SetLayoutDirty();
01979         }
