02012             int end = start + length;
02013             for (; i < end; i++)
02014             {
02015                 if (sourceText[i] == 92 && i < length - 1)
02016                 {
02017                     switch ((int)sourceText[i + 1])
02018                     {
02019                         case 110: // \n LineFeed
02020                             if (writeIndex == m_char_buffer.Length) ResizeInternalArray(ref m_char_buffer);
