02027                             if (writeIndex == m_char_buffer.Length) ResizeInternalArray(ref m_char_buffer);
02028 
02029                             m_char_buffer[writeIndex] = (char)13;
02030                             i += 1;
02031                             writeIndex += 1;
02032                             continue;
02033                         case 116: // \t Tab
02034                             if (writeIndex == m_char_buffer.Length) ResizeInternalArray(ref m_char_buffer);
02035 
02036                             m_char_buffer[writeIndex] = (char)9;
02037                             i += 1;
02038                             writeIndex += 1;
02039                             continue;
02040                     }
02041                 }
02042 
02043                 // Handle inline replacement of <stlye> and <br> tags.
02044                 if (sourceText[i] == 60)
02045                 {
02046                     if (IsTagName(ref sourceText, "<BR>", i))
02047                     {
02048                         if (writeIndex == m_char_buffer.Length) ResizeInternalArray(ref m_char_buffer);
02049 
02050                         m_char_buffer[writeIndex] = 10; ;
02051                         writeIndex += 1;
02052                         i += 3;
02053 
02054                         continue;
02055                     }
02056                     else if (IsTagName(ref sourceText, "<STYLE=", i))
02057                     {
02058                         int srcOffset = 0;
02059                         if (ReplaceOpeningStyleTag(ref sourceText, i, out srcOffset, ref m_char_buffer, ref writeIndex))
02060                         {
02061                             i = srcOffset;
02062                             continue;
02063                         }
02064                     }
02065                     else if (IsTagName(ref sourceText, "</STYLE>", i))
02066                     {
02067                         ReplaceClosingStyleTag(ref sourceText, i, ref m_char_buffer, ref writeIndex);
02068 
02069                         // Strip </style> even if style is invalid.
02070                         i += 7;
02071                         continue;
02072                     }
02073                 }
02074 
02075                 if (writeIndex == m_char_buffer.Length) ResizeInternalArray(ref m_char_buffer);
02076 
02077                 m_char_buffer[writeIndex] = sourceText[i];
02078                 writeIndex += 1;
02079             }
02080 
02081             if (writeIndex == m_char_buffer.Length) ResizeInternalArray(ref m_char_buffer);
02082 
02083             m_char_buffer[writeIndex] = (char)0;
02084 
02085             m_inputSource = TextInputSources.SetCharArray;
02086             m_havePropertiesChanged = true;
02087             m_isInputParsingRequired = true;
02088             m_isCalculateSizeRequired = true;
