02205 
02211         protected void SetTextArrayToCharArray(char[] sourceText, ref int[] charBuffer)
02212         {
02213             //Debug.Log("SetText Array to Char called.");
02214             if (sourceText == null || m_charArray_Length == 0)
02215                 return;
02216 
02217             if (charBuffer == null) charBuffer = new int[8];
02218 
02219             // Clear the Style stack.
02220             m_styleStack.Clear();
02221 
02222             int writeIndex = 0;
02223 
02224             for (int i = 0; i < m_charArray_Length; i++)
02225             {
02226                 // Handle UTF-32 in the input text (string).
02227                 if (char.IsHighSurrogate(sourceText[i]) && char.IsLowSurrogate(sourceText[i + 1]))
02228                 {
02229                     if (writeIndex == charBuffer.Length) ResizeInternalArray(ref charBuffer);
02230 
02231                     charBuffer[writeIndex] = char.ConvertToUtf32(sourceText[i], sourceText[i + 1]);
02232                     i += 1;
02233                     writeIndex += 1;
02234                     continue;
02235                 }
02236 
02237                 // Handle inline replacement of <stlye> and <br> tags.
02238                 if (sourceText[i] == 60)
02239                 {
02240                     if (IsTagName(ref sourceText, "<BR>", i))
02241                     {
02242                         if (writeIndex == charBuffer.Length) ResizeInternalArray(ref charBuffer);
02243 
02244                         charBuffer[writeIndex] = 10;
02245                         writeIndex += 1;
02246                         i += 3;
02247 
02248                         continue;
02249                     }
02250                     else if (IsTagName(ref sourceText, "<STYLE=", i))
02251                     {
02252                         int srcOffset = 0;
02253                         if (ReplaceOpeningStyleTag(ref sourceText, i, out srcOffset, ref charBuffer, ref writeIndex))
02254                         {
02255                             i = srcOffset;
02256                             continue;
02257                         }
02258                     }
02259                     else if (IsTagName(ref sourceText, "</STYLE>", i))
02260                     {
02261                         ReplaceClosingStyleTag(ref sourceText, i, ref charBuffer, ref writeIndex);
02262 
02263                         // Strip </style> even if style is invalid.
02264                         i += 7;
02265                         continue;
02266                     }
02267                 }
02268 
02269                 if (writeIndex == charBuffer.Length) ResizeInternalArray(ref charBuffer);
02270 
02271                 charBuffer[writeIndex] = sourceText[i];
02272                 writeIndex += 1;
02273             }
02274 
02275             if (writeIndex == charBuffer.Length) ResizeInternalArray(ref charBuffer);
02276 
02277             charBuffer[writeIndex] = 0;
02278         }
02279 
02280 
02286         protected void StringToCharArray(string sourceText, ref int[] charBuffer)
02287         {
02288             if (sourceText == null)
02289             {
02290                 charBuffer[0] = 0;
02291                 return;
02292             }
02293 
02294             if (charBuffer == null) charBuffer = new int[8];
02295 
02296             // Clear the Style stack.
02297             m_styleStack.SetDefault(0);
02298 
02299             int writeIndex = 0;
02300 
02301             for (int i = 0; i < sourceText.Length; i++)
02302             {
02303                 if (m_inputSource == TextInputSources.Text && sourceText[i] == 92 && sourceText.Length > i + 1)
02304                 {
02305                     switch ((int)sourceText[i + 1])
02306                     {
02307                         case 85: // \U00000000 for UTF-32 Unicode
02308                             if (sourceText.Length > i + 9)
02309                             {
02310                                 if (writeIndex == charBuffer.Length) ResizeInternalArray(ref charBuffer);
02311 
02312                                 charBuffer[writeIndex] = GetUTF32(sourceText, i + 2);
02313                                 i += 9;
02314                                 writeIndex += 1;
02315                                 continue;
02316                             }
02317                             break;
02318                         case 92: // \ escape
02319                             if (!m_parseCtrlCharacters) break;
02320 
02321                             if (sourceText.Length <= i + 2) break;
02322 
02323                             if (writeIndex + 2 > charBuffer.Length) ResizeInternalArray(ref charBuffer);
02324 
02325                             charBuffer[writeIndex] = sourceText[i + 1];
02326                             charBuffer[writeIndex + 1] = sourceText[i + 2];
02327                             i += 2;
02328                             writeIndex += 2;
02329                             continue;
02330                         case 110: // \n LineFeed
02331                             if (!m_parseCtrlCharacters) break;
02332 
02333                             if (writeIndex == charBuffer.Length) ResizeInternalArray(ref charBuffer);
02334 
02335                             charBuffer[writeIndex] = (char)10;
02336                             i += 1;
02337                             writeIndex += 1;
02338                             continue;
02339                         case 114: // \r
02340                             if (!m_parseCtrlCharacters) break;
02341 
02342                             if (writeIndex == charBuffer.Length) ResizeInternalArray(ref charBuffer);
02343 
02344                             charBuffer[writeIndex] = (char)13;
02345                             i += 1;
02346                             writeIndex += 1;
02347                             continue;
02348                         case 116: // \t Tab
02349                             if (!m_parseCtrlCharacters) break;
02350 
02351                             if (writeIndex == charBuffer.Length) ResizeInternalArray(ref charBuffer);
02352 
02353                             charBuffer[writeIndex] = (char)9;
02354                             i += 1;
02355                             writeIndex += 1;
02356                             continue;
02357                         case 117: // \u0000 for UTF-16 Unicode
02358                             if (sourceText.Length > i + 5)
02359                             {
02360                                 if (writeIndex == charBuffer.Length) ResizeInternalArray(ref charBuffer);
02361 
02362                                 charBuffer[writeIndex] = (char)GetUTF16(sourceText, i + 2);
02363                                 i += 5;
02364                                 writeIndex += 1;
02365                                 continue;
02366                             }
02367                             break;
02368                     }
02369                 }
02370 
02371                 // Handle UTF-32 in the input text (string). // Not sure this is needed //
02372                 if (char.IsHighSurrogate(sourceText[i]) && char.IsLowSurrogate(sourceText[i + 1]))
02373                 {
02374                     if (writeIndex == charBuffer.Length) ResizeInternalArray(ref charBuffer);
02375 
02376                     charBuffer[writeIndex] = char.ConvertToUtf32(sourceText[i], sourceText[i + 1]);
02377                     i += 1;
02378                     writeIndex += 1;
02379                     continue;
02380                 }
02381 
02383                 if (sourceText[i] == 60 && m_isRichText)
02384                 {
02385                     if (IsTagName(ref sourceText, "<BR>", i))
02386                     {
02387                         if (writeIndex == charBuffer.Length) ResizeInternalArray(ref charBuffer);
02388 
02389                         charBuffer[writeIndex] = 10; ;
02390                         writeIndex += 1;
02391                         i += 3;
02392 
02393                         continue;
02394                     }
02395                     else if (IsTagName(ref sourceText, "<STYLE=", i))
02396                     {
02397                         int srcOffset = 0;
02398                         if (ReplaceOpeningStyleTag(ref sourceText, i, out srcOffset, ref charBuffer, ref writeIndex))
02399                         {
02400                             i = srcOffset;
02401                             continue;
02402                         }
02403                     }
02404                     else if (IsTagName(ref sourceText, "</STYLE>", i))
02405                     {
02406                         ReplaceClosingStyleTag(ref sourceText, i, ref charBuffer, ref writeIndex);
02407 
02408                         // Strip </style> even if style is invalid.
02409                         i += 7;
02410                         continue;
02411                     }
02412                 }
02413 
02414                 if (writeIndex == charBuffer.Length) ResizeInternalArray(ref charBuffer);
02415 
02416                 charBuffer[writeIndex] = sourceText[i];
02417                 writeIndex += 1;
02418             }
02419 
02420             if (writeIndex == charBuffer.Length) ResizeInternalArray(ref charBuffer);
02421 
02422             charBuffer[writeIndex] = (char)0;
02423         }
02424 
02425 
02431         protected void StringBuilderToIntArray(StringBuilder sourceText, ref int[] charBuffer)
02432         {
02433             if (sourceText == null)
02434             {
02435                 charBuffer[0] = 0;
02436                 return;
02437             }
02438 
02439             if (charBuffer == null) charBuffer = new int[8];
02440 
02441             // Clear the Style stack.
02442             m_styleStack.Clear();
02443 
02444             #if UNITY_EDITOR
02445             // Create new string to be displayed in the Input Text Box of the Editor Panel.
02446             m_text = sourceText.ToString();
02447             #endif
02448 
02449             int writeIndex = 0;
02450 
02451             for (int i = 0; i < sourceText.Length; i++)
02452             {
02453                 if (m_parseCtrlCharacters && sourceText[i] == 92 && sourceText.Length > i + 1)
02454                 {
02455                     switch ((int)sourceText[i + 1])
02456                     {
02457                         case 85: // \U00000000 for UTF-32 Unicode
02458                             if (sourceText.Length > i + 9)
02459                             {
02460                                 if (writeIndex == charBuffer.Length) ResizeInternalArray(ref charBuffer);
02461 
02462                                 charBuffer[writeIndex] = GetUTF32(sourceText, i + 2);
02463                                 i += 9;
02464                                 writeIndex += 1;
02465                                 continue;
02466                             }
02467                             break;
02468                         case 92: // \ escape
02469                             if (sourceText.Length <= i + 2) break;
02470 
02471                             if (writeIndex + 2 > charBuffer.Length) ResizeInternalArray(ref charBuffer);
02472 
02473                             charBuffer[writeIndex] = sourceText[i + 1];
02474                             charBuffer[writeIndex + 1] = sourceText[i + 2];
02475                             i += 2;
02476                             writeIndex += 2;
02477                             continue;
02478                         case 110: // \n LineFeed
02479                             if (writeIndex == charBuffer.Length) ResizeInternalArray(ref charBuffer);
02480 
02481                             charBuffer[writeIndex] = (char)10;
02482                             i += 1;
02483                             writeIndex += 1;
02484                             continue;
02485                         case 114: // \r
02486                             if (writeIndex == charBuffer.Length) ResizeInternalArray(ref charBuffer);
02487 
02488                             charBuffer[writeIndex] = (char)13;
02489                             i += 1;
02490                             writeIndex += 1;
02491                             continue;
02492                         case 116: // \t Tab
02493                             if (writeIndex == charBuffer.Length) ResizeInternalArray(ref charBuffer);
02494 
02495                             charBuffer[writeIndex] = (char)9;
02496                             i += 1;
02497                             writeIndex += 1;
02498                             continue;
02499                         case 117: // \u0000 for UTF-16 Unicode
02500                             if (sourceText.Length > i + 5)
02501                             {
02502                                 if (writeIndex == charBuffer.Length) ResizeInternalArray(ref charBuffer);
02503 
02504                                 charBuffer[writeIndex] = (char)GetUTF16(sourceText, i + 2);
02505                                 i += 5;
02506                                 writeIndex += 1;
02507                                 continue;
02508                             }
02509                             break;
02510                     }
02511                 }
02512 
02513                 // Handle UTF-32 in the input text (string).
02514                 if (char.IsHighSurrogate(sourceText[i]) && char.IsLowSurrogate(sourceText[i + 1]))
02515                 {
02516                     if (writeIndex == charBuffer.Length) ResizeInternalArray(ref charBuffer);
02517 
02518                     charBuffer[writeIndex] = char.ConvertToUtf32(sourceText[i], sourceText[i + 1]);
02519                     i += 1;
02520                     writeIndex += 1;
02521                     continue;
02522                 }
02523 
02524                 // Handle inline replacement of <stlye> and <br> tags.
02525                 if (sourceText[i] == 60)
02526                 {
02527                     if (IsTagName(ref sourceText, "<BR>", i))
02528                     {
02529                         if (writeIndex == charBuffer.Length) ResizeInternalArray(ref charBuffer);
02530 
02531                         charBuffer[writeIndex] = 10;
02532                         writeIndex += 1;
02533                         i += 3;
02534 
02535                         continue;
02536                     }
02537                     else if (IsTagName(ref sourceText, "<STYLE=", i))
02538                     {
02539                         int srcOffset = 0;
02540                         if (ReplaceOpeningStyleTag(ref sourceText, i, out srcOffset, ref charBuffer, ref writeIndex))
02541                         {
02542                             i = srcOffset;
02543                             continue;
02544                         }
02545                     }
02546                     else if (IsTagName(ref sourceText, "</STYLE>", i))
02547                     {
02548                         ReplaceClosingStyleTag(ref sourceText, i, ref charBuffer, ref writeIndex);
02549 
02550                         // Strip </style> even if style is invalid.
02551                         i += 7;
02552                         continue;
02553                     }
02554                 }
02555 
02556                 if (writeIndex == charBuffer.Length) ResizeInternalArray(ref charBuffer);
02557 
02558                 charBuffer[writeIndex] = sourceText[i];
02559                 writeIndex += 1;
02560             }
02561 
02562             if (writeIndex == charBuffer.Length) ResizeInternalArray(ref charBuffer);
02563 
02564             charBuffer[writeIndex] = (char)0;
02565         }
02566 
02567 
02577         bool ReplaceOpeningStyleTag(ref string sourceText, int srcIndex, out int srcOffset, ref int[] charBuffer, ref int writeIndex)
02578         {
02579             // Validate <style> tag.
02580             int hashCode = GetTagHashCode(ref sourceText, srcIndex + 7, out srcOffset);
02581 
02582             TMP_Style style = TMP_StyleSheet.GetStyle(hashCode);
02583 
02584             // Return if we don't have a valid style.
02585             if (style == null || srcOffset == 0) return false;
02586 
02587             m_styleStack.Add(style.hashCode);
02588 
02589             int styleLength = style.styleOpeningTagArray.Length;
02590 
02591             // Replace <style> tag with opening definition
02592             int[] openingTagArray = style.styleOpeningTagArray;
02593 
02594             for (int i = 0; i < styleLength; i++)
02595             {
02596                 int c = openingTagArray[i];
02597 
02598                 if (c == 60)
02599                 {
02600                     if (IsTagName(ref openingTagArray, "<BR>", i))
02601                     {
02602                         if (writeIndex == charBuffer.Length) ResizeInternalArray(ref charBuffer);
02603 
02604                         charBuffer[writeIndex] = 10;
02605                         writeIndex += 1;
02606                         i += 3;
02607 
02608                         continue;
02609                     }
02610                     else if (IsTagName(ref openingTagArray, "<STYLE=", i))
02611                     {
02612                         int offset = 0;
02613                         if (ReplaceOpeningStyleTag(ref openingTagArray, i, out offset, ref charBuffer, ref writeIndex))
02614                         {
02615                             i = offset;
02616                             continue;
02617                         }
02618                     }
02619                     else if (IsTagName(ref openingTagArray, "</STYLE>", i))
02620                     {
02621                         ReplaceClosingStyleTag(ref openingTagArray, i, ref charBuffer, ref writeIndex);
02622                         
02623                         // Strip </style> even if style is invalid.
02624                         i += 7;
02625                         continue;
02626                     }
02627                 }
02628 
02629                 if (writeIndex == charBuffer.Length) ResizeInternalArray(ref charBuffer);
02630 
02631                 charBuffer[writeIndex] = c;
02632                 writeIndex += 1;
02633             }
02634 
02635             return true;
02636         }
02637 
