02860         bool ReplaceClosingStyleTag(ref string sourceText, int srcIndex, ref int[] charBuffer, ref int writeIndex)
02861         {
02862             // Get style from the Style Stack
02863             int hashCode = m_styleStack.CurrentItem();
02864             TMP_Style style = TMP_StyleSheet.GetStyle(hashCode);
02865 
02866             m_styleStack.Remove();
02867 
02868             // Return if we don't have a valid style.
02869             if (style == null) return false;
02870 
02871             int styleLength = style.styleClosingTagArray.Length;
02872 
02873             // Replace <style> tag with opening definition
02874             int[] closingTagArray = style.styleClosingTagArray;
02875 
02876             for (int i = 0; i < styleLength; i++)
02877             {
02878                 int c = closingTagArray[i];
02879 
02880                 if (c == 60)
02881                 {
02882                     if (IsTagName(ref closingTagArray, "<BR>", i))
02883                     {
02884                         if (writeIndex == charBuffer.Length) ResizeInternalArray(ref charBuffer);
02885 
02886                         charBuffer[writeIndex] = 10;
02887                         writeIndex += 1;
02888                         i += 3;
02889 
02890                         continue;
02891                     }
02892                     else if (IsTagName(ref closingTagArray, "<STYLE=", i))
02893                     {
02894                         int offset = 0;
02895                         if (ReplaceOpeningStyleTag(ref closingTagArray, i, out offset, ref charBuffer, ref writeIndex))
02896                         {
02897                             i = offset;
02898                             continue;
02899                         }
02900                     }
02901                     else if (IsTagName(ref closingTagArray, "</STYLE>", i))
02902                     {
02903                         ReplaceClosingStyleTag(ref closingTagArray, i, ref charBuffer, ref writeIndex);
02904 
02905                         // Strip </style> even if style is invalid.
02906                         i += 7;
02907                         continue;
02908                     }
02909                 }
02910 
02911                 if (writeIndex == charBuffer.Length) ResizeInternalArray(ref charBuffer);
02912 
02913                 charBuffer[writeIndex] = c;
02914                 writeIndex += 1;
02915             }
02916 
02917             return true;
02918         }
