03134         bool IsTagName (ref string text, string tag, int index)
03135         {
03136             if (text.Length < index + tag.Length) return false;
03137             
03138             for (int i = 0; i < tag.Length; i++)
03139             {
03140                 if (TMP_TextUtilities.ToUpperFast(text[index + i]) != tag[i]) return false;
03141             }
03142 
03143             return true;
03144         }
03145 
03153         bool IsTagName(ref char[] text, string tag, int index)
03154         {
03155             if (text.Length < index + tag.Length) return false;
03156 
03157             for (int i = 0; i < tag.Length; i++)
03158             {
03159                 if (TMP_TextUtilities.ToUpperFast(text[index + i]) != tag[i]) return false;
03160             }
03161 
03162             return true;
03163         }
03164 
03172         bool IsTagName(ref int[] text, string tag, int index)
03173         {
03174             if (text.Length < index + tag.Length) return false;
03175 
03176             for (int i = 0; i < tag.Length; i++)
03177             {
03178                 if (TMP_TextUtilities.ToUpperFast((char)text[index + i]) != tag[i]) return false;
03179             }
03180 
03181             return true;
03182         }
