03191         bool IsTagName(ref StringBuilder text, string tag, int index)
03192         {
03193             if (text.Length < index + tag.Length) return false;
03194 
03195             for (int i = 0; i < tag.Length; i++)
03196             {
03197                 if (TMP_TextUtilities.ToUpperFast(text[index + i]) != tag[i]) return false;
03198             }
03199 
03200             return true;
03201         }
03202 
03210         int GetTagHashCode(ref string text, int index, out int closeIndex)
03211         {
03212             int hashCode = 0;
03213             closeIndex = 0;
03214 
03215             for (int i = index; i < text.Length; i++)
03216             {
03217                 // Skip quote '"' character
03218                 if (text[i] == 34) continue;
03219 
03220                 // Break at '>'
03221                 if (text[i] == 62) { closeIndex = i; break; }
03222 
03223                 hashCode = (hashCode << 5) + hashCode ^ text[i];
03224             }
03225 
03226             return hashCode;
03227         }
03228 
03236         int GetTagHashCode(ref char[] text, int index, out int closeIndex)
03237         {
03238             int hashCode = 0;
03239             closeIndex = 0;
03240 
03241             for (int i = index; i < text.Length; i++)
03242             {
03243                 // Skip quote '"' character
03244                 if (text[i] == 34) continue;
03245 
03246                 // Break at '>'
03247                 if (text[i] == 62) { closeIndex = i; break; }
03248 
03249                 hashCode = (hashCode << 5) + hashCode ^ text[i];
03250             }
03251 
03252             return hashCode;
03253         }
03254 
03262         int GetTagHashCode(ref int[] text, int index, out int closeIndex)
03263         {
03264             int hashCode = 0;
03265             closeIndex = 0;
03266 
03267             for (int i = index; i < text.Length; i++)
03268             {
03269                 // Skip quote '"' character
03270                 if (text[i] == 34) continue;
03271 
03272                 // Break at '>'
03273                 if (text[i] == 62) { closeIndex = i; break; }
03274 
03275                 hashCode = (hashCode << 5) + hashCode ^ text[i];
03276             }
03277 
03278             return hashCode;
03279         }
03280 
03288         int GetTagHashCode(ref StringBuilder text, int index, out int closeIndex)
03289         {
03290             int hashCode = 0;
03291             closeIndex = 0;
03292 
03293             for (int i = index; i < text.Length; i++)
03294             {
03295                 // Skip quote '"' character
03296                 if (text[i] == 34) continue;
03297 
03298                 // Break at '>'
03299                 if (text[i] == 62) { closeIndex = i; break; }
03300 
03301                 hashCode = (hashCode << 5) + hashCode ^ text[i];
03302             }
03303 
03304             return hashCode;
03305         }
03306 
03310         void ResizeInternalArray <T>(ref T[] array)
03311         {
03312             int size = Mathf.NextPowerOfTwo(array.Length + 1);
03313 
03314             System.Array.Resize(ref array, size);
03315         }
03316 
03317 
03318         private readonly float[] k_Power = { 5e-1f, 5e-2f, 5e-3f, 5e-4f, 5e-5f, 5e-6f, 5e-7f, 5e-8f, 5e-9f, 5e-10f }; // Used by FormatText to enable rounding and avoid using Mathf.Pow.
03319 
03326         protected void AddFloatToCharArray(float number, ref int index, int precision)
03327         {
03328             if (number < 0)
03329             {
03330                 m_input_CharArray[index++] = '-';
03331                 number = -number;
03332             }
03333 
03334             number += k_Power[Mathf.Min(9, precision)];
03335 
03336             int integer = (int)number;
03337             AddIntToCharArray(integer, ref index, precision);
03338 
03339             if (precision > 0)
03340             {
03341                 // Add the decimal point
03342                 m_input_CharArray[index++] = '.';
03343 
03344                 number -= integer;
03345                 for (int p = 0; p < precision; p++)
03346                 {
03347                     number *= 10;
03348                     int d = (int)(number);
03349 
03350                     m_input_CharArray[index++] = (char)(d + 48);
03351                     number -= d;
03352                 }
03353             }
03354         }
03355 
03356 
03363         protected void AddIntToCharArray(int number, ref int index, int precision)
03364         {
03365             if (number < 0)
03366             {
03367                 m_input_CharArray[index++] = '-';
03368                 number = -number;
03369             }
03370 
03371             int i = index;
03372             do
03373             {
03374                 m_input_CharArray[i++] = (char)(number % 10 + 48);
03375                 number /= 10;
03376             } while (number > 0);
03377 
03378             int lastIndex = i;
03379 
03380             // Reverse string
03381             while (index + 1 < i)
03382             {
03383                 i -= 1;
03384                 char t = m_input_CharArray[index];
03385                 m_input_CharArray[index] = m_input_CharArray[i];
03386                 m_input_CharArray[i] = t;
03387                 index += 1;
03388             }
03389             index = lastIndex;
03390         }
