03505         protected float GetPreferredWidth()
03506         {
03507             if (TMP_Settings.instance == null) return 0;
03508 
03509             float fontSize = m_enableAutoSizing ? m_fontSizeMax : m_fontSize;
03510 
03511             // Reset auto sizing point size bounds
03512             m_minFontSize = m_fontSizeMin;
03513             m_maxFontSize = m_fontSizeMax;
03514             m_charWidthAdjDelta = 0;
03515 
03516             // Set Margins to Infinity
03517             Vector2 margin = k_LargePositiveVector2;
03518 
03519             if (m_isInputParsingRequired || m_isTextTruncated)
03520             {
03521                 m_isCalculatingPreferredValues = true;
03522                 ParseInputText();
03523             }
03524 
03525             m_recursiveCount = 0;
03526             float preferredWidth = CalculatePreferredValues(fontSize, margin, true).x;
03527 
03528             m_isPreferredWidthDirty = false;
03529 
03530             //Debug.Log("GetPreferredWidth() Called at frame " + Time.frameCount + ". Returning width of " + preferredWidth);
03531 
03532             return preferredWidth;
03533         }
