03672 
03677         protected virtual Vector2 CalculatePreferredValues(float defaultFontSize, Vector2 marginSize, bool ignoreTextAutoSizing)
03678         {
03679             //Debug.Log("*** CalculatePreferredValues() ***"); // ***** Frame: " + Time.frameCount);
03680 
03682 
03683             // Early exit if no font asset was assigned. This should not be needed since LiberationSans SDF will be assigned by default.
03684             if (m_fontAsset == null || m_fontAsset.characterDictionary == null)
03685             {
03686                 Debug.LogWarning("Can't Generate Mesh! No Font Asset has been assigned to Object ID: " + this.GetInstanceID());
03687 
03688                 return Vector2.zero;
03689             }
03690 
03691             // Early exit if we don't have any Text to generate.
03692             if (m_char_buffer == null || m_char_buffer.Length == 0 || m_char_buffer[0] == (char)0)
03693             {
03694                 return Vector2.zero;
03695             }
03696 
03697             m_currentFontAsset = m_fontAsset;
03698             m_currentMaterial = m_sharedMaterial;
03699             m_currentMaterialIndex = 0;
03700             m_materialReferenceStack.SetDefault(new MaterialReference(0, m_currentFontAsset, null, m_currentMaterial, m_padding));
03701 
03702             // Total character count is computed when the text is parsed.
03703             int totalCharacterCount = m_totalCharacterCount; // m_VisibleCharacters.Count;
03704 
03705             if (m_internalCharacterInfo == null || totalCharacterCount > m_internalCharacterInfo.Length)
03706             {
03707                 m_internalCharacterInfo = new TMP_CharacterInfo[totalCharacterCount > 1024 ? totalCharacterCount + 256 : Mathf.NextPowerOfTwo(totalCharacterCount)];
03708             }
03709 
03710             // Calculate the scale of the font based on selected font size and sampling point size.
03711             // baseScale is calculated using the font asset assigned to the text object.
03712             float baseScale = m_fontScale = (defaultFontSize / m_fontAsset.fontInfo.PointSize * m_fontAsset.fontInfo.Scale * (m_isOrthographic ? 1 : 0.1f));
03713             float currentElementScale = baseScale;
03714             m_fontScaleMultiplier = 1;
03715 
03716             m_currentFontSize = defaultFontSize;
03717             m_sizeStack.SetDefault(m_currentFontSize);
03718             float fontSizeDelta = 0;
03719 
03720             int charCode = 0; // Holds the character code of the currently being processed character.
03721 
03722             m_style = m_fontStyle; // Set the default style.
03723 
03724             m_lineJustification = m_textAlignment; // Sets the line justification mode to match editor alignment.
03725             m_lineJustificationStack.SetDefault(m_lineJustification);
03726 
03727             float bold_xAdvance_multiplier = 1; // Used to increase spacing between character when style is bold.
03728 
03729             m_baselineOffset = 0; // Used by subscript characters.
03730             m_baselineOffsetStack.Clear();
03731 
03732             m_lineOffset = 0; // Amount of space between lines (font line spacing + m_linespacing).
03733             m_lineHeight = TMP_Math.FLOAT_UNSET;
03734             float lineGap = m_currentFontAsset.fontInfo.LineHeight - (m_currentFontAsset.fontInfo.Ascender - m_currentFontAsset.fontInfo.Descender);
03735 
03736             m_cSpacing = 0; // Amount of space added between characters as a result of the use of the <cspace> tag.
03737             m_monoSpacing = 0;
03738             float lineOffsetDelta = 0;
03739             m_xAdvance = 0; // Used to track the position of each character.
03740             float maxXAdvance = 0; // Used to determine Preferred Width.
03741 
03742             tag_LineIndent = 0; // Used for indentation of text.
03743             tag_Indent = 0;
03744             m_indentStack.SetDefault(0);
03745             tag_NoParsing = false;
03746             //m_isIgnoringAlignment = false;
03747 
03748             m_characterCount = 0; // Total characters in the char[]
03749 
03750 
03751             // Tracking of line information
03752             m_firstCharacterOfLine = 0;
03753             m_maxLineAscender = k_LargeNegativeFloat;
03754             m_maxLineDescender = k_LargePositiveFloat;
03755             m_lineNumber = 0;
03756 
03757             float marginWidth = marginSize.x;
03758             //float marginHeight = marginSize.y;
03759             m_marginLeft = 0;
03760             m_marginRight = 0;
03761             m_width = -1;
03762 
03763             // Used by Unity's Auto Layout system.
03764             float renderedWidth = 0;
03765             float renderedHeight = 0;
03766             float linebreakingWidth = 0;
03767             m_isCalculatingPreferredValues = true;
03768 
03769             // Tracking of the highest Ascender
03770             m_maxAscender = 0;
03771             m_maxDescender = 0;
03772 
03773 
03774             // Initialize struct to track states of word wrapping
03775             bool isFirstWord = true;
03776             bool isLastBreakingChar = false;
03777             WordWrapState savedLineState = new WordWrapState();
03778             SaveWordWrappingState(ref savedLineState, 0, 0);
03779             WordWrapState savedWordWrapState = new WordWrapState();
03780             int wrappingIndex = 0;
03781 
03782             // Counter to prevent recursive lockup when computing preferred values.
03783             m_recursiveCount += 1;
03784 
03785             int endTagIndex = 0;
03786             // Parse through Character buffer to read HTML tags and begin creating mesh.
03787             for (int i = 0; m_char_buffer[i] != 0; i++)
03788             {
03789                 charCode = m_char_buffer[i];
03790 
03791                 // Parse Rich Text Tag
03792                 #region Parse Rich Text Tag
03793                 if (m_isRichText && charCode == 60)  // '<'
03794                 {
03795                     m_isParsingText = true;
03796                     m_textElementType = TMP_TextElementType.Character;
03797 
03798                     // Check if Tag is valid. If valid, skip to the end of the validated tag.
03799                     if (ValidateHtmlTag(m_char_buffer, i + 1, out endTagIndex))
03800                     {
03801                         i = endTagIndex;
03802 
03803                         // Continue to next character or handle the sprite element
03804                         if (m_textElementType == TMP_TextElementType.Character)
03805                             continue;
03806                     }
03807                 }
03808                 else
03809                 {
03810                     m_textElementType = m_textInfo.characterInfo[m_characterCount].elementType;
03811                     m_currentMaterialIndex = m_textInfo.characterInfo[m_characterCount].materialReferenceIndex;
03812                     m_currentFontAsset = m_textInfo.characterInfo[m_characterCount].fontAsset;
03813                 }
03814                 #endregion End Parse Rich Text Tag
03815 
03816                 int prev_MaterialIndex = m_currentMaterialIndex;
03817                 bool isUsingAltTypeface = m_textInfo.characterInfo[m_characterCount].isUsingAlternateTypeface;
03818 
03819                 m_isParsingText = false;
03820 
03821                 // Handle Font Styles like LowerCase, UpperCase and SmallCaps.
03822                 #region Handling of LowerCase, UpperCase and SmallCaps Font Styles
03823 
03824                 float smallCapsMultiplier = 1.0f;
03825 
03826                 if (m_textElementType == TMP_TextElementType.Character)
03827                 {
03828                     if ((m_style & FontStyles.UpperCase) == FontStyles.UpperCase)
03829                     {
03830                         // If this character is lowercase, switch to uppercase.
03831                         if (char.IsLower((char)charCode))
03832                             charCode = char.ToUpper((char)charCode);
03833 
03834                     }
03835                     else if ((m_style & FontStyles.LowerCase) == FontStyles.LowerCase)
03836                     {
03837                         // If this character is uppercase, switch to lowercase.
03838                         if (char.IsUpper((char)charCode))
03839                             charCode = char.ToLower((char)charCode);
03840                     }
03841                     else if ((m_fontStyle & FontStyles.SmallCaps) == FontStyles.SmallCaps || (m_style & FontStyles.SmallCaps) == FontStyles.SmallCaps)
03842                     {
03843                         if (char.IsLower((char)charCode))
03844                         {
03845                             smallCapsMultiplier = 0.8f;
03846                             charCode = char.ToUpper((char)charCode);
03847                         }
03848                     }
03849                 }
03850                 #endregion
03851 
03852 
03853                 // Look up Character Data from Dictionary and cache it.
03854                 #region Look up Character Data
03855                 if (m_textElementType == TMP_TextElementType.Sprite)
03856                 {
03857                     // If a sprite is used as a fallback then get a reference to it and set the color to white.
03858                     m_currentSpriteAsset = m_textInfo.characterInfo[m_characterCount].spriteAsset;
03859                     m_spriteIndex = m_textInfo.characterInfo[m_characterCount].spriteIndex;
03860 
03861                     TMP_Sprite sprite = m_currentSpriteAsset.spriteInfoList[m_spriteIndex];
03862                     if (sprite == null) continue;
03863 
03864                     // Sprites are assigned in the E000 Private Area + sprite Index
03865                     if (charCode == 60)
03866                         charCode = 57344 + m_spriteIndex;
03867 
03868                     m_currentFontAsset = m_fontAsset;
03869 
03870                     // The sprite scale calculations are based on the font asset assigned to the text object.
03871                     // Sprite scale is used to determine line height
03872                     // Current element scale represents a modified scale to normalize the sprite based on the font baseline to ascender.
03873                     float spriteScale = (m_currentFontSize / m_fontAsset.fontInfo.PointSize * m_fontAsset.fontInfo.Scale * (m_isOrthographic ? 1 : 0.1f));
03874                     currentElementScale = m_fontAsset.fontInfo.Ascender / sprite.height * sprite.scale * spriteScale;
03875 
03876                     m_cached_TextElement = sprite;
03877 
03878                     m_internalCharacterInfo[m_characterCount].elementType = TMP_TextElementType.Sprite;
03879                     m_internalCharacterInfo[m_characterCount].scale = spriteScale;
03880                     //m_internalCharacterInfo[m_characterCount].spriteAsset = m_currentSpriteAsset;
03881                     //m_internalCharacterInfo[m_characterCount].fontAsset = m_currentFontAsset;
03882                     //m_internalCharacterInfo[m_characterCount].materialReferenceIndex = m_currentMaterialIndex;
03883 
03884                     m_currentMaterialIndex = prev_MaterialIndex;
03885                 }
03886                 else if (m_textElementType == TMP_TextElementType.Character)
03887                 {
03888                     m_cached_TextElement = m_textInfo.characterInfo[m_characterCount].textElement;
03889                     if (m_cached_TextElement == null) continue;
03890 
03891                     //m_currentFontAsset = m_textInfo.characterInfo[m_characterCount].fontAsset;
03892                     //m_currentMaterial = m_textInfo.characterInfo[m_characterCount].material;
03893                     m_currentMaterialIndex = m_textInfo.characterInfo[m_characterCount].materialReferenceIndex;
03894 
03895                     // Re-calculate font scale as the font asset may have changed.
03896                     m_fontScale = m_currentFontSize * smallCapsMultiplier / m_currentFontAsset.fontInfo.PointSize * m_currentFontAsset.fontInfo.Scale * (m_isOrthographic ? 1 : 0.1f);
03897 
03898                     currentElementScale = m_fontScale * m_fontScaleMultiplier * m_cached_TextElement.scale;
03899 
03900                     m_internalCharacterInfo[m_characterCount].elementType = TMP_TextElementType.Character;
03901 
03902                 }
03903                 #endregion
03904 
03905 
03906                 // Handle Soft Hyphen
03907                 #region Handle Soft Hyphen
03908                 float old_scale = currentElementScale;
03909                 if (charCode == 0xAD)
03910                 {
03911                     currentElementScale = 0;
03912                 }
03913                 #endregion
03914 
03915 
03916                 // Store some of the text object's information
03917                 m_internalCharacterInfo[m_characterCount].character = (char)charCode;
03918 
03919 
03920                 // Handle Kerning if Enabled.
03921                 #region Handle Kerning
03922                 GlyphValueRecord glyphAdjustments = new GlyphValueRecord();
03923                 if (m_enableKerning)
03924                 {
03925                     KerningPair adjustmentPair = null;
03926 
03927                     if (m_characterCount < totalCharacterCount - 1)
03928                     {
03929                         uint nextGlyph = m_textInfo.characterInfo[m_characterCount + 1].character;
03930                         KerningPairKey keyValue = new KerningPairKey((uint)charCode, nextGlyph);
03931 
03932                         m_currentFontAsset.kerningDictionary.TryGetValue((int)keyValue.key, out adjustmentPair);
03933                         if (adjustmentPair != null)
03934                             glyphAdjustments = adjustmentPair.firstGlyphAdjustments;
03935                     }
03936 
03937                     if (m_characterCount >= 1)
03938                     {
03939                         uint previousGlyph = m_textInfo.characterInfo[m_characterCount - 1].character;
03940                         KerningPairKey keyValue = new KerningPairKey(previousGlyph, (uint)charCode);
03941 
03942                         m_currentFontAsset.kerningDictionary.TryGetValue((int)keyValue.key, out adjustmentPair);
03943                         if (adjustmentPair != null)
03944                             glyphAdjustments += adjustmentPair.secondGlyphAdjustments;
03945                     }
03946                 }
03947                 #endregion
03948 
03949 
03950                 // Initial Implementation for RTL support.
03951                 #region Handle Right-to-Left
03952                 //if (m_isRightToLeft)
03953                 //{
03954                 //    m_xAdvance -= ((m_cached_TextElement.xAdvance * bold_xAdvance_multiplier + m_characterSpacing + m_wordSpacing + m_currentFontAsset.normalSpacingOffset) * currentElementScale + m_cSpacing) * (1 - m_charWidthAdjDelta);
03955 
03956                 //    if (char.IsWhiteSpace((char)charCode) || charCode == 0x200B)
03957                 //        m_xAdvance -= m_wordSpacing * currentElementScale;
03958                 //}
03959                 #endregion
03960 
03961 
03962                 // Handle Mono Spacing
03963                 #region Handle Mono Spacing
03964                 float monoAdvance = 0;
03965                 if (m_monoSpacing != 0)
03966                 {
03967                     monoAdvance = (m_monoSpacing / 2 - (m_cached_TextElement.width / 2 + m_cached_TextElement.xOffset) * currentElementScale);
03968                     m_xAdvance += monoAdvance;
03969                 }
03970                 #endregion
03971 
03972 
03973                 // Set Padding based on selected font style
03974                 #region Handle Style Padding
03975                 if (m_textElementType == TMP_TextElementType.Character && !isUsingAltTypeface && ((m_style & FontStyles.Bold) == FontStyles.Bold || (m_fontStyle & FontStyles.Bold) == FontStyles.Bold)) // Checks for any combination of Bold Style.
03976                 {
03977                     //style_padding = m_currentFontAsset.boldStyle * 2;
03978                     bold_xAdvance_multiplier = 1 + m_currentFontAsset.boldSpacing * 0.01f;
03979                 }
03980                 else
03981                 {
03982                     //style_padding = m_currentFontAsset.normalStyle * 2;
03983                     bold_xAdvance_multiplier = 1.0f;
03984                 }
03985                 #endregion Handle Style Padding
03986 
03987                 m_internalCharacterInfo[m_characterCount].baseLine = 0 - m_lineOffset + m_baselineOffset;
03988 
03989 
03990                 // Compute and save text element Ascender and maximum line Ascender.
03991                 float elementAscender = m_currentFontAsset.fontInfo.Ascender * (m_textElementType == TMP_TextElementType.Character ? currentElementScale / smallCapsMultiplier : m_internalCharacterInfo[m_characterCount].scale) + m_baselineOffset;
03992                 m_internalCharacterInfo[m_characterCount].ascender = elementAscender - m_lineOffset;
03993                 m_maxLineAscender = elementAscender > m_maxLineAscender ? elementAscender : m_maxLineAscender;
03994 
03995                 // Compute and save text element Descender and maximum line Descender.
03996                 float elementDescender = m_currentFontAsset.fontInfo.Descender * (m_textElementType == TMP_TextElementType.Character ? currentElementScale / smallCapsMultiplier: m_internalCharacterInfo[m_characterCount].scale) + m_baselineOffset;
03997                 float elementDescenderII = m_internalCharacterInfo[m_characterCount].descender = elementDescender - m_lineOffset;
03998                 m_maxLineDescender = elementDescender < m_maxLineDescender ? elementDescender : m_maxLineDescender;
03999 
04000                 // Adjust maxLineAscender and maxLineDescender if style is superscript or subscript
04001                 if ((m_style & FontStyles.Subscript) == FontStyles.Subscript || (m_style & FontStyles.Superscript) == FontStyles.Superscript)
04002                 {
04003                     float baseAscender = (elementAscender - m_baselineOffset) / m_currentFontAsset.fontInfo.SubSize;
04004                     elementAscender = m_maxLineAscender;
04005                     m_maxLineAscender = baseAscender > m_maxLineAscender ? baseAscender : m_maxLineAscender;
04006 
04007                     float baseDescender = (elementDescender - m_baselineOffset) / m_currentFontAsset.fontInfo.SubSize;
04008                     elementDescender = m_maxLineDescender;
04009                     m_maxLineDescender = baseDescender < m_maxLineDescender ? baseDescender : m_maxLineDescender;
04010                 }
04011 
04012                 if (m_lineNumber == 0) m_maxAscender = m_maxAscender > elementAscender ? m_maxAscender : elementAscender;
04013                 //if (m_lineOffset == 0) pageAscender = pageAscender > elementAscender ? pageAscender : elementAscender;
04014 
04015                 // Setup Mesh for visible text elements. ie. not a SPACE / LINEFEED / CARRIAGE RETURN.
04016                 #region Handle Visible Characters
04017                 if (charCode == 9 || charCode == 0xA0 || charCode == 0x2007 || (!char.IsWhiteSpace((char)charCode) && charCode != 0x200B) || m_textElementType == TMP_TextElementType.Sprite)
04018                 {
04019                     // Check if Character exceeds the width of the Text Container
04020                     #region Handle Line Breaking, Text Auto-Sizing and Horizontal Overflow
04021                     float width = m_width != -1 ? Mathf.Min(marginWidth + 0.0001f - m_marginLeft - m_marginRight, m_width) : marginWidth + 0.0001f - m_marginLeft - m_marginRight;
04022 
04023                     bool isJustifiedOrFlush = ((_HorizontalAlignmentOptions)m_lineJustification & _HorizontalAlignmentOptions.Flush) == _HorizontalAlignmentOptions.Flush || ((_HorizontalAlignmentOptions)m_lineJustification & _HorizontalAlignmentOptions.Justified) == _HorizontalAlignmentOptions.Justified;
04024 
04025                     // Calculate the line breaking width of the text.
04026                     linebreakingWidth = m_xAdvance + m_cached_TextElement.xAdvance * (1 - m_charWidthAdjDelta) * (charCode != 0xAD ? currentElementScale : old_scale);
04027 
04028                     // Check if Character exceeds the width of the Text Container
04029                     if (linebreakingWidth > width * (isJustifiedOrFlush ? 1.05f : 1.0f))
04030                     {
04031                         // Word Wrapping
04032                         #region Handle Word Wrapping
04033                         if (enableWordWrapping && m_characterCount != m_firstCharacterOfLine)
04034                         {
04035                             // Check if word wrapping is still possible
04036                             #region Line Breaking Check
04037                             if (wrappingIndex == savedWordWrapState.previous_WordBreak || isFirstWord)
04038                             {
04039                                 // Word wrapping is no longer possible. Shrink size of text if auto-sizing is enabled.
04040                                 #region Text Auto-Sizing
04041                                 if (ignoreTextAutoSizing == false && m_currentFontSize > m_fontSizeMin)
04042                                 {
04043                                     // Handle Character Width Adjustments
04044                                     #region Character Width Adjustments
04045                                     if (m_charWidthAdjDelta < m_charWidthMaxAdj / 100)
04046                                     {
04047                                         m_recursiveCount = 0;
04048                                         m_charWidthAdjDelta += 0.01f;
04049                                         return CalculatePreferredValues(defaultFontSize, marginSize, false);
04050                                     }
04051                                     #endregion
04052 
04053                                     // Adjust Point Size
04054                                     m_maxFontSize = defaultFontSize;
04055 
04056                                     defaultFontSize -= Mathf.Max((defaultFontSize - m_minFontSize) / 2, 0.05f);
04057                                     defaultFontSize = (int)(Mathf.Max(defaultFontSize, m_fontSizeMin) * 20 + 0.5f) / 20f;
04058 
04059                                     if (m_recursiveCount > 20) return new Vector2(renderedWidth, renderedHeight);
04060                                     return CalculatePreferredValues(defaultFontSize, marginSize, false);
04061                                 }
04062                                 #endregion
04063 
04064                                 // Word wrapping is no longer possible, now breaking up individual words.
04065                                 if (m_isCharacterWrappingEnabled == false)
04066                                 {
04067                                     m_isCharacterWrappingEnabled = true;
04068                                 }
04069                                 else
04070                                     isLastBreakingChar = true;
04071                             }
04072                             #endregion
04073 
04074                             // Restore to previously stored state of last valid (space character or linefeed)
04075                             i = RestoreWordWrappingState(ref savedWordWrapState);
04076                             wrappingIndex = i;  // Used to detect when line length can no longer be reduced.
04077 
04078                             // Handling for Soft Hyphen
04079                             if (m_char_buffer[i] == 0xAD) // && !m_isCharacterWrappingEnabled) // && ellipsisIndex != i && !m_isCharacterWrappingEnabled)
04080                             {
04081                                 m_isTextTruncated = true;
04082                                 m_char_buffer[i] = 0x2D;
04083                                 return CalculatePreferredValues(defaultFontSize, marginSize, true);
04084                             }
04085 
04086                             // Check if Line Spacing of previous line needs to be adjusted.
04087                             if (m_lineNumber > 0 && !TMP_Math.Approximately(m_maxLineAscender, m_startOfLineAscender) && m_lineHeight == TMP_Math.FLOAT_UNSET)
04088                             {
04089                                 //Debug.Log("(1) Adjusting Line Spacing on line #" + m_lineNumber);
04090                                 float offsetDelta = m_maxLineAscender - m_startOfLineAscender;
04091                                 //AdjustLineOffset(m_firstCharacterOfLine, m_characterCount, offsetDelta);
04092                                 m_lineOffset += offsetDelta;
04093                                 savedWordWrapState.lineOffset = m_lineOffset;
04094                                 savedWordWrapState.previousLineAscender = m_maxLineAscender;
04095 
04096                                 // TODO - Add check for character exceeding vertical bounds
04097                             }
04098                             //m_isNewPage = false;
04099 
04100                             // Calculate lineAscender & make sure if last character is superscript or subscript that we check that as well.
04101                             float lineAscender = m_maxLineAscender - m_lineOffset;
04102                             float lineDescender = m_maxLineDescender - m_lineOffset;
04103 
04104 
04105                             // Update maxDescender and maxVisibleDescender
04106                             m_maxDescender = m_maxDescender < lineDescender ? m_maxDescender : lineDescender;
04107 
04108 
04109                             m_firstCharacterOfLine = m_characterCount; // Store first character of the next line.
04110 
04111                             // Compute Preferred Width & Height
04112                             renderedWidth += m_xAdvance;
04113 
04114                             if (m_enableWordWrapping)
04115                                 renderedHeight = m_maxAscender - m_maxDescender;
04116                             else
04117                                 renderedHeight = Mathf.Max(renderedHeight, lineAscender - lineDescender);
04118 
04119 
04120                             // Store the state of the line before starting on the new line.
04121                             SaveWordWrappingState(ref savedLineState, i, m_characterCount - 1);
04122 
04123                             m_lineNumber += 1;
04124                             //isStartOfNewLine = true;
04125 
04126                             // Check to make sure Array is large enough to hold a new line.
04127                             //if (m_lineNumber >= m_internalTextInfo.lineInfo.Length)
04128                             //    ResizeLineExtents(m_lineNumber);
04129 
04130                             // Apply Line Spacing based on scale of the last character of the line.
04131                             if (m_lineHeight == TMP_Math.FLOAT_UNSET)
04132                             {
04133                                 float ascender = m_internalCharacterInfo[m_characterCount].ascender - m_internalCharacterInfo[m_characterCount].baseLine;
04134                                 lineOffsetDelta = 0 - m_maxLineDescender + ascender + (lineGap + m_lineSpacing + m_lineSpacingDelta) * baseScale;
04135                                 m_lineOffset += lineOffsetDelta;
04136 
04137                                 m_startOfLineAscender = ascender;
04138                             }
04139                             else
04140                                 m_lineOffset += m_lineHeight + m_lineSpacing * baseScale;
04141 
04142                             m_maxLineAscender = k_LargeNegativeFloat;
04143                             m_maxLineDescender = k_LargePositiveFloat;
04144 
04145                             m_xAdvance = 0 + tag_Indent;
04146 
04147                             continue;
04148                         }
04149                         #endregion End Word Wrapping
04150 
04151                         // Text Auto-Sizing (text exceeding Width of container. 
04152                         #region Handle Text Auto-Sizing
04153                         if (ignoreTextAutoSizing == false && defaultFontSize > m_fontSizeMin)
04154                         {
04155                             // Handle Character Width Adjustments
04156                             #region Character Width Adjustments
04157                             if (m_charWidthAdjDelta < m_charWidthMaxAdj / 100)
04158                             {
04159                                 m_recursiveCount = 0;
04160                                 m_charWidthAdjDelta += 0.01f;
04161                                 return CalculatePreferredValues(defaultFontSize, marginSize, false);
04162                             }
04163                             #endregion
04164 
04165                             // Adjust Point Size
04166                             m_maxFontSize = defaultFontSize;
04167 
04168                             defaultFontSize -= Mathf.Max((defaultFontSize - m_minFontSize) / 2, 0.05f);
04169                             defaultFontSize = (int)(Mathf.Max(defaultFontSize, m_fontSizeMin) * 20 + 0.5f) / 20f;
04170 
04171                             if (m_recursiveCount > 20) return new Vector2(renderedWidth, renderedHeight);
04172                             return CalculatePreferredValues(defaultFontSize, marginSize, false);
04173                         }
04174                         #endregion End Text Auto-Sizing
04175                     }
04176                     #endregion End Check for Characters Exceeding Width of Text Container
04177 
04178                 }
04179                 #endregion Handle Visible Characters
04180 
04181 
04182                 // Check if Line Spacing of previous line needs to be adjusted.
04183                 #region Adjust Line Spacing
04184                 if (m_lineNumber > 0 && !TMP_Math.Approximately(m_maxLineAscender, m_startOfLineAscender) && m_lineHeight == TMP_Math.FLOAT_UNSET && !m_isNewPage)
04185                 {
04186                     //Debug.Log("Inline - Adjusting Line Spacing on line #" + m_lineNumber);
04187                     //float gap = 0; // Compute gap.
04188 
04189                     float offsetDelta = m_maxLineAscender - m_startOfLineAscender;
04190                     //AdjustLineOffset(m_firstCharacterOfLine, m_characterCount, offsetDelta);
04191                     elementDescenderII -= offsetDelta;
04192                     m_lineOffset += offsetDelta;
04193 
04194                     m_startOfLineAscender += offsetDelta;
04195                     savedWordWrapState.lineOffset = m_lineOffset;
04196                     savedWordWrapState.previousLineAscender = m_startOfLineAscender;
04197                 }
04198                 #endregion
04199 
04200 
04201                 // Check if text Exceeds the vertical bounds of the margin area.
04202                 #region Check Vertical Bounds & Auto-Sizing
04203                 /*
04204                 if (m_maxAscender - elementDescenderII > marginHeight + 0.0001f)
04205                 {
04206                     // Handle Line spacing adjustments
04207                     #region Line Spacing Adjustments
04208                     if (m_enableAutoSizing && m_lineSpacingDelta > m_lineSpacingMax && m_lineNumber > 0)
04209                     {
04210                         //loopCountA = 0;
04211 
04212                         //m_lineSpacingDelta -= 1;
04213                         //GenerateTextMesh();
04214                         //return;
04215                     }
04216                     #endregion
04217 
04218 
04219                     // Handle Text Auto-sizing resulting from text exceeding vertical bounds.
04220                     #region Text Auto-Sizing (Text greater than vertical bounds)
04221                     if (m_enableAutoSizing && m_fontSize > m_fontSizeMin)
04222                     {
04223                         m_maxFontSize = m_fontSize;
04224 
04225                         m_fontSize -= Mathf.Max((m_fontSize - m_minFontSize) / 2, 0.05f);
04226                         m_fontSize = (int)(Mathf.Max(m_fontSize, m_fontSizeMin) * 20 + 0.5f) / 20f;
04227 
04228                         //m_recursiveCount = 0;
04229                         //if (loopCountA > 20) return; // Added to debug 
04230                         CalculatePreferredValues(m_fontSize, marginSize, false);
04231                         return Vector2.zero;
04232                     }
04233                     #endregion Text Auto-Sizing
04234                 }
04235                 */
04236                 #endregion Check Vertical Bounds
04237 
04238 
04239                 // Handle xAdvance & Tabulation Stops. Tab stops at every 25% of Font Size.
04240                 #region XAdvance, Tabulation & Stops
04241                 if (charCode == 9)
04242                 {
04243                     float tabSize = m_currentFontAsset.fontInfo.TabWidth * currentElementScale;
04244                     float tabs = Mathf.Ceil(m_xAdvance / tabSize) * tabSize;
04245                     m_xAdvance = tabs > m_xAdvance ? tabs : m_xAdvance + tabSize;
04246                 }
04247                 else if (m_monoSpacing != 0)
04248                 {
04249                     m_xAdvance += (m_monoSpacing - monoAdvance + ((m_characterSpacing + m_currentFontAsset.normalSpacingOffset) * currentElementScale) + m_cSpacing) * (1 - m_charWidthAdjDelta);
04250 
04251                     if (char.IsWhiteSpace((char)charCode) || charCode == 0x200B)
04252                         m_xAdvance += m_wordSpacing * currentElementScale;
04253                 }
04254                 else
04255                 {
04256                     m_xAdvance += ((m_cached_TextElement.xAdvance * bold_xAdvance_multiplier + m_characterSpacing + m_currentFontAsset.normalSpacingOffset + glyphAdjustments.xAdvance) * currentElementScale + m_cSpacing) * (1 - m_charWidthAdjDelta);
04257 
04258                     if (char.IsWhiteSpace((char)charCode) || charCode == 0x200B)
04259                         m_xAdvance += m_wordSpacing * currentElementScale;
04260                 }
04261 
04262 
04263                 #endregion Tabulation & Stops
04264 
04265 
04266                 // Handle Carriage Return
04267                 #region Carriage Return
04268                 if (charCode == 13)
04269                 {
04270                     maxXAdvance = Mathf.Max(maxXAdvance, renderedWidth + m_xAdvance);
04271                     renderedWidth = 0;
04272                     m_xAdvance = 0 + tag_Indent;
04273                 }
04274                 #endregion Carriage Return
04275 
04276 
04277                 // Handle Line Spacing Adjustments + Word Wrapping & special case for last line.
04278                 #region Check for Line Feed and Last Character
04279                 if (charCode == 10 || m_characterCount == totalCharacterCount - 1)
04280                 {
04281                     // Check if Line Spacing of previous line needs to be adjusted.
04282                     if (m_lineNumber > 0 && !TMP_Math.Approximately(m_maxLineAscender, m_startOfLineAscender) && m_lineHeight == TMP_Math.FLOAT_UNSET)
04283                     {
04284                         //Debug.Log("(2) Adjusting Line Spacing on line #" + m_lineNumber);
04285                         float offsetDelta = m_maxLineAscender - m_startOfLineAscender;
04286                         //AdjustLineOffset(m_firstCharacterOfLine, m_characterCount, offsetDelta);
04287                         elementDescenderII -= offsetDelta;
04288                         m_lineOffset += offsetDelta;
04289                     }
04290 
04291                     // Calculate lineAscender & make sure if last character is superscript or subscript that we check that as well.
04292                     //float lineAscender = m_maxLineAscender - m_lineOffset;
04293                     float lineDescender = m_maxLineDescender - m_lineOffset;
04294 
04295                     // Update maxDescender and maxVisibleDescender
04296                     m_maxDescender = m_maxDescender < lineDescender ? m_maxDescender : lineDescender;
04297 
04298                     m_firstCharacterOfLine = m_characterCount + 1;
04299 
04300                     // Store PreferredWidth paying attention to linefeed and last character of text.
04301                     if (charCode == 10 && m_characterCount != totalCharacterCount - 1)
04302                     {
04303                         maxXAdvance = Mathf.Max(maxXAdvance, renderedWidth + linebreakingWidth);
04304                         renderedWidth = 0;
04305                     }
04306                     else
04307                         renderedWidth = Mathf.Max(maxXAdvance, renderedWidth + linebreakingWidth);
04308 
04309 
04310                     renderedHeight = m_maxAscender - m_maxDescender;
04311 
04312 
04313                     // Add new line if not last lines or character.
04314                     if (charCode == 10)
04315                     {
04316                         // Store the state of the line before starting on the new line.
04317                         SaveWordWrappingState(ref savedLineState, i, m_characterCount);
04318                         // Store the state of the last Character before the new line.
04319                         SaveWordWrappingState(ref savedWordWrapState, i, m_characterCount);
04320 
04321                         m_lineNumber += 1;
04322 
04323                         // Apply Line Spacing
04324                         if (m_lineHeight == TMP_Math.FLOAT_UNSET)
04325                         {
04326                             lineOffsetDelta = 0 - m_maxLineDescender + elementAscender + (lineGap + m_lineSpacing + m_paragraphSpacing + m_lineSpacingDelta) * baseScale;
04327                             m_lineOffset += lineOffsetDelta;
04328                         }
04329                         else
04330                             m_lineOffset += m_lineHeight + (m_lineSpacing + m_paragraphSpacing) * baseScale;
04331 
04332                         m_maxLineAscender = k_LargeNegativeFloat;
04333                         m_maxLineDescender = k_LargePositiveFloat;
04334                         m_startOfLineAscender = elementAscender;
04335 
04336                         m_xAdvance = 0 + tag_LineIndent + tag_Indent;
04337 
04338                         m_characterCount += 1;
04339                         continue;
04340                     }
04341                 }
04342                 #endregion Check for Linefeed or Last Character
04343 
04344 
04345                 // Save State of Mesh Creation for handling of Word Wrapping
04346                 #region Save Word Wrapping State
04347                 if (m_enableWordWrapping || m_overflowMode == TextOverflowModes.Truncate || m_overflowMode == TextOverflowModes.Ellipsis)
04348                 {
04349                     if ((char.IsWhiteSpace((char)charCode) || charCode == 0x200B || charCode == 0x2D || charCode == 0xAD) && !m_isNonBreakingSpace && charCode != 0xA0 && charCode != 0x2011 && charCode != 0x202F && charCode != 0x2060)
04350                     {
04351                         // We store the state of numerous variables for the most recent Space, LineFeed or Carriage Return to enable them to be restored 
04352                         // for Word Wrapping.
04353                         SaveWordWrappingState(ref savedWordWrapState, i, m_characterCount);
04354                         m_isCharacterWrappingEnabled = false;
04355                         isFirstWord = false;
04356                     }
04357                     // Handling for East Asian languages
04358                     else if ((charCode > 0x1100 && charCode < 0x11ff || /* Hangul Jamo */
04359                                 charCode > 0x2E80 && charCode < 0x9FFF || /* CJK */
04360                                 charCode > 0xA960 && charCode < 0xA97F || /* Hangul Jame Extended-A */
04361                                 charCode > 0xAC00 && charCode < 0xD7FF || /* Hangul Syllables */
04362                                 charCode > 0xF900 && charCode < 0xFAFF || /* CJK Compatibility Ideographs */
04363                                 charCode > 0xFE30 && charCode < 0xFE4F || /* CJK Compatibility Forms */
04364                                 charCode > 0xFF00 && charCode < 0xFFEF)   /* CJK Halfwidth */
04365                                 && !m_isNonBreakingSpace)
04366                     {
04367                         if (isFirstWord || isLastBreakingChar || TMP_Settings.linebreakingRules.leadingCharacters.ContainsKey(charCode) == false &&
04368                             (m_characterCount < totalCharacterCount - 1 &&
04369                             TMP_Settings.linebreakingRules.followingCharacters.ContainsKey(m_internalCharacterInfo[m_characterCount + 1].character) == false))
04370                         {
04371                             SaveWordWrappingState(ref savedWordWrapState, i, m_characterCount);
04372                             m_isCharacterWrappingEnabled = false;
04373                             isFirstWord = false;
04374                         }
04375                     }
04376                     else if ((isFirstWord || m_isCharacterWrappingEnabled == true || isLastBreakingChar))
04377                         SaveWordWrappingState(ref savedWordWrapState, i, m_characterCount);
04378                 }
04379                 #endregion Save Word Wrapping State
04380 
04381                 m_characterCount += 1;
04382             }
04383 
04384             // Check Auto Sizing and increase font size to fill text container.
04385             #region Check Auto-Sizing (Upper Font Size Bounds)
04386             fontSizeDelta = m_maxFontSize - m_minFontSize;
04387             if (!m_isCharacterWrappingEnabled && ignoreTextAutoSizing == false && fontSizeDelta > 0.051f && defaultFontSize < m_fontSizeMax)
04388             {
04389                 m_minFontSize = defaultFontSize;
04390                 defaultFontSize += Mathf.Max((m_maxFontSize - defaultFontSize) / 2, 0.05f);
04391                 defaultFontSize = (int)(Mathf.Min(defaultFontSize, m_fontSizeMax) * 20 + 0.5f) / 20f;
04392 
04393                 if (m_recursiveCount > 20) return new Vector2(renderedWidth, renderedHeight);
04394                 return CalculatePreferredValues(defaultFontSize, marginSize, false);
04395             }
04396             #endregion End Auto-sizing Check
04397 
04398 
04399             m_isCharacterWrappingEnabled = false;
04400             m_isCalculatingPreferredValues = false;
04401 
04402             // Adjust Preferred Width and Height to account for Margins.
04403             renderedWidth += m_margin.x > 0 ? m_margin.x : 0;
04404             renderedWidth += m_margin.z > 0 ? m_margin.z : 0;
04405 
04406             renderedHeight += m_margin.y > 0 ? m_margin.y : 0;
04407             renderedHeight += m_margin.w > 0 ? m_margin.w : 0;
04408 
04409             // Round Preferred Values to nearest 5/100.
04410             renderedWidth = (int)(renderedWidth * 100 + 1f) / 100f;
04411             renderedHeight = (int)(renderedHeight * 100 + 1f) / 100f;
04412 
04413             //Debug.Log("Preferred Values: (" + renderedWidth + ", " + renderedHeight + ") with Recursive count of " + m_recursiveCount);
04414 
04416             return new Vector2(renderedWidth, renderedHeight);
04417         }
04418 
04419 
04424         protected virtual Bounds GetCompoundBounds() { return new Bounds(); }
04425 
04426 
04431         protected Bounds GetTextBounds()
04432         {
04433             if (m_textInfo == null || m_textInfo.characterCount > m_textInfo.characterInfo.Length) return new Bounds();
04434 
04435             Extents extent = new Extents(k_LargePositiveVector2, k_LargeNegativeVector2);
04436 
04437             for (int i = 0; i < m_textInfo.characterCount && i < m_textInfo.characterInfo.Length; i++)
04438             {
04439                 if (!m_textInfo.characterInfo[i].isVisible) continue;
04440 
04441                 extent.min.x = Mathf.Min(extent.min.x, m_textInfo.characterInfo[i].bottomLeft.x);
04442                 extent.min.y = Mathf.Min(extent.min.y, m_textInfo.characterInfo[i].descender);
04443 
04444                 extent.max.x = Mathf.Max(extent.max.x, m_textInfo.characterInfo[i].xAdvance);
04445                 extent.max.y = Mathf.Max(extent.max.y, m_textInfo.characterInfo[i].ascender);
04446             }
04447 
04448             Vector2 size;
04449             size.x = extent.max.x - extent.min.x;
04450             size.y = extent.max.y - extent.min.y;
04451 
04452             Vector3 center = (extent.min + extent.max) / 2;
04453 
04454             return new Bounds(center, size);
04455         }
04456 
04457 
04463         protected Bounds GetTextBounds(bool onlyVisibleCharacters)
04464         {
04465             if (m_textInfo == null) return new Bounds();
04466 
04467             Extents extent = new Extents(k_LargePositiveVector2, k_LargeNegativeVector2);
04468 
04469             for (int i = 0; i < m_textInfo.characterCount; i++)
04470             {
04471                 if ((i > maxVisibleCharacters || m_textInfo.characterInfo[i].lineNumber > m_maxVisibleLines) && onlyVisibleCharacters) break;
04472 
04473                 if (onlyVisibleCharacters && !m_textInfo.characterInfo[i].isVisible) continue;
04474 
04475                 extent.min.x = Mathf.Min(extent.min.x, m_textInfo.characterInfo[i].origin);
04476                 extent.min.y = Mathf.Min(extent.min.y, m_textInfo.characterInfo[i].descender);
04477 
04478                 extent.max.x = Mathf.Max(extent.max.x, m_textInfo.characterInfo[i].xAdvance);
04479                 extent.max.y = Mathf.Max(extent.max.y, m_textInfo.characterInfo[i].ascender);
04480             }
04481 
04482             Vector2 size;
04483             size.x = extent.max.x - extent.min.x;
04484             size.y = extent.max.y - extent.min.y;
04485 
04486             Vector2 center = (extent.min + extent.max) / 2;
04487 
04488             return new Bounds(center, size);
04489         }
