04498         protected virtual void AdjustLineOffset(int startIndex, int endIndex, float offset) { }
04499 
04500 
04505         protected void ResizeLineExtents(int size)
04506         {
04507             size = size > 1024 ? size + 256 : Mathf.NextPowerOfTwo(size + 1);
04508 
04509             TMP_LineInfo[] temp_lineInfo = new TMP_LineInfo[size];
04510             for (int i = 0; i < size; i++)
04511             {
04512                 if (i < m_textInfo.lineInfo.Length)
04513                     temp_lineInfo[i] = m_textInfo.lineInfo[i];
04514                 else
04515                 {
04516                     temp_lineInfo[i].lineExtents.min = k_LargePositiveVector2;
04517                     temp_lineInfo[i].lineExtents.max = k_LargeNegativeVector2;
04518 
04519                     temp_lineInfo[i].ascender = k_LargeNegativeFloat;
04520                     temp_lineInfo[i].descender = k_LargePositiveFloat;
04521                 }
04522             }
04523 
04524             m_textInfo.lineInfo = temp_lineInfo;
04525         }
04526         protected static Vector2 k_LargePositiveVector2 = new Vector2(TMP_Math.INT_MAX, TMP_Math.INT_MAX);
