04604         protected void SaveWordWrappingState(ref WordWrapState state, int index, int count)
04605         {
04606             // Multi Font & Material support related
04607             state.currentFontAsset = m_currentFontAsset;
04608             state.currentSpriteAsset = m_currentSpriteAsset;
