04614             state.visible_CharacterCount = m_lineVisibleCharacterCount;
04615             //state.visible_CharacterCount = m_visibleCharacterCount;
04616             //state.visible_SpriteCount = m_visibleSpriteCount;
04617             state.visible_LinkCount = m_textInfo.linkCount;
04618 
