04620             state.firstVisibleCharacterIndex = m_firstVisibleCharacterOfLine;
04621             state.lastVisibleCharIndex = m_lastVisibleCharacterOfLine;
04622 
04623             state.fontStyle = m_style;
04624             state.fontScale = m_fontScale;
04625             //state.maxFontScale = m_maxFontScale;
04626             state.fontScaleMultiplier = m_fontScaleMultiplier;
04627             state.currentFontSize = m_currentFontSize;
04628 
04629             state.xAdvance = m_xAdvance;
04630             state.maxCapHeight = m_maxCapHeight;
04631             state.maxAscender = m_maxAscender;
04632             state.maxDescender = m_maxDescender;
04633             state.maxLineAscender = m_maxLineAscender;
04634             state.maxLineDescender = m_maxLineDescender;
04635             state.previousLineAscender = m_startOfLineAscender;
04636             state.preferredWidth = m_preferredWidth;
