04682         protected int RestoreWordWrappingState(ref WordWrapState state)
04683         {
04684             int index = state.previous_WordBreak;
04685 
04686             // Multi Font & Material support related
04687             m_currentFontAsset = state.currentFontAsset;
04688             m_currentSpriteAsset = state.currentSpriteAsset;
04689             m_currentMaterial = state.currentMaterial;
04690             m_currentMaterialIndex = state.currentMaterialIndex;
04691 
04692             m_characterCount = state.total_CharacterCount + 1;
04693             m_lineVisibleCharacterCount = state.visible_CharacterCount;
04694             //m_visibleCharacterCount = state.visible_CharacterCount;
04695             //m_visibleSpriteCount = state.visible_SpriteCount;
