04704             m_fontScaleMultiplier = state.fontScaleMultiplier;
04705             //m_maxFontScale = state.maxFontScale;
04706             m_currentFontSize = state.currentFontSize;
04707 
04708             m_xAdvance = state.xAdvance;
04709             m_maxCapHeight = state.maxCapHeight;
04710             m_maxAscender = state.maxAscender;
04711             m_maxDescender = state.maxDescender;
04712             m_maxLineAscender = state.maxLineAscender;
04713             m_maxLineDescender = state.maxLineDescender;
04714             m_startOfLineAscender = state.previousLineAscender;
04715             m_preferredWidth = state.preferredWidth;
04716             m_preferredHeight = state.preferredHeight;
04717             m_meshExtents = state.meshExtents;
04718 
04719             m_lineNumber = state.lineNumber;
04720             m_lineOffset = state.lineOffset;
04721             m_baselineOffset = state.baselineOffset;
04722 
04723             //m_lineJustification = state.alignment;
04724             m_htmlColor = state.vertexColor;
04725             m_underlineColor = state.underlineColor;
04726             m_strikethroughColor = state.strikethroughColor;
04727             m_highlightColor = state.highlightColor;
04728 
