04733             m_fontStyleStack = state.basicStyleStack;
04734             m_colorStack = state.colorStack;
04735             m_underlineColorStack = state.underlineColorStack;
04736             m_strikethroughColorStack = state.strikethroughColorStack;
04737             m_highlightColorStack = state.highlightColorStack;
04738             m_colorGradientStack = state.colorGradientStack;
