04744             m_actionStack = state.actionStack;
04745             m_materialReferenceStack = state.materialReferenceStack;
04746             m_lineJustificationStack = state.lineJustificationStack;
04747             //m_spriteAnimationStack = state.spriteAnimationStack;
04748 
04749             m_spriteAnimationID = state.spriteAnimationID;
04750 
04751             if (m_lineNumber < m_textInfo.lineInfo.Length)
04752                 m_textInfo.lineInfo[m_lineNumber] = state.lineInfo;
04753 
04754             return index;
04755         }
