04763         protected virtual void SaveGlyphVertexInfo(float padding, float style_padding, Color32 vertexColor)
04764         {
04765             // Save the Vertex Position for the Character
04766             #region Setup Mesh Vertices
04767             m_textInfo.characterInfo[m_characterCount].vertex_BL.position = m_textInfo.characterInfo[m_characterCount].bottomLeft;
04768             m_textInfo.characterInfo[m_characterCount].vertex_TL.position = m_textInfo.characterInfo[m_characterCount].topLeft;
04769             m_textInfo.characterInfo[m_characterCount].vertex_TR.position = m_textInfo.characterInfo[m_characterCount].topRight;
04770             m_textInfo.characterInfo[m_characterCount].vertex_BR.position = m_textInfo.characterInfo[m_characterCount].bottomRight;
04771             #endregion
04772 
04773 
04774             #region Setup Vertex Colors
04775             // Alpha is the lower of the vertex color or tag color alpha used.
04776             vertexColor.a = m_fontColor32.a < vertexColor.a ? (byte)(m_fontColor32.a) : (byte)(vertexColor.a);
04777 
04778             // Handle Vertex Colors & Vertex Color Gradient
04779             if (!m_enableVertexGradient)
04780             {
04781                 m_textInfo.characterInfo[m_characterCount].vertex_BL.color = vertexColor;
04782                 m_textInfo.characterInfo[m_characterCount].vertex_TL.color = vertexColor;
04783                 m_textInfo.characterInfo[m_characterCount].vertex_TR.color = vertexColor;
04784                 m_textInfo.characterInfo[m_characterCount].vertex_BR.color = vertexColor;
04785             }
04786             else
04787             {
04788                 if (!m_overrideHtmlColors && m_colorStack.index > 1)
04789                 {
04790                     m_textInfo.characterInfo[m_characterCount].vertex_BL.color = vertexColor;
04791                     m_textInfo.characterInfo[m_characterCount].vertex_TL.color = vertexColor;
04792                     m_textInfo.characterInfo[m_characterCount].vertex_TR.color = vertexColor;
04793                     m_textInfo.characterInfo[m_characterCount].vertex_BR.color = vertexColor;
04794                 }
04795                 else // Handle Vertex Color Gradient
04796                 {
04797                     // Use Vertex Color Gradient Preset (if one is assigned)
04798                     if (m_fontColorGradientPreset != null)
04799                     {
04800                         m_textInfo.characterInfo[m_characterCount].vertex_BL.color = m_fontColorGradientPreset.bottomLeft * vertexColor;
04801                         m_textInfo.characterInfo[m_characterCount].vertex_TL.color = m_fontColorGradientPreset.topLeft * vertexColor;
04802                         m_textInfo.characterInfo[m_characterCount].vertex_TR.color = m_fontColorGradientPreset.topRight * vertexColor;
04803                         m_textInfo.characterInfo[m_characterCount].vertex_BR.color = m_fontColorGradientPreset.bottomRight * vertexColor;
04804                     }
04805                     else
04806                     {
04807                         m_textInfo.characterInfo[m_characterCount].vertex_BL.color = m_fontColorGradient.bottomLeft * vertexColor;
04808                         m_textInfo.characterInfo[m_characterCount].vertex_TL.color = m_fontColorGradient.topLeft * vertexColor;
04809                         m_textInfo.characterInfo[m_characterCount].vertex_TR.color = m_fontColorGradient.topRight * vertexColor;
04810                         m_textInfo.characterInfo[m_characterCount].vertex_BR.color = m_fontColorGradient.bottomRight * vertexColor;
04811                     }
04812                 }
04813             }
04814 
04815             if (m_colorGradientPreset != null)
04816             {
04817                 m_textInfo.characterInfo[m_characterCount].vertex_BL.color *= m_colorGradientPreset.bottomLeft;
04818                 m_textInfo.characterInfo[m_characterCount].vertex_TL.color *= m_colorGradientPreset.topLeft;
04819                 m_textInfo.characterInfo[m_characterCount].vertex_TR.color *= m_colorGradientPreset.topRight;
04820                 m_textInfo.characterInfo[m_characterCount].vertex_BR.color *= m_colorGradientPreset.bottomRight;
04821             }
04822             #endregion
04823 
04824             // Apply style_padding only if this is a SDF Shader.
04825             if (!m_isSDFShader)
04826                 style_padding = 0;
04827 
04828 
04829             // Setup UVs for the Character
04830             #region Setup UVs
04831             FaceInfo faceInfo = m_currentFontAsset.fontInfo;
04832 
04833             Vector2 uv0;
04834             uv0.x = (m_cached_TextElement.x - padding - style_padding) / faceInfo.AtlasWidth;
04835             uv0.y = 1 - (m_cached_TextElement.y + padding + style_padding + m_cached_TextElement.height) / faceInfo.AtlasHeight;
04836 
04837             Vector2 uv1;
04838             uv1.x = uv0.x;
04839             uv1.y = 1 - (m_cached_TextElement.y - padding - style_padding) / faceInfo.AtlasHeight;
04840 
04841             Vector2 uv2;
04842             uv2.x = (m_cached_TextElement.x + padding + style_padding + m_cached_TextElement.width) / faceInfo.AtlasWidth;
04843             uv2.y = uv1.y;
04844 
04845             Vector2 uv3;
04846             uv3.x = uv2.x;
04847             uv3.y = uv0.y;
04848 
04849             //Vector2 uv0 = new Vector2((m_cached_TextElement.x - padding - style_padding) / faceInfo.AtlasWidth, 1 - (m_cached_TextElement.y + padding + style_padding + m_cached_TextElement.height) / faceInfo.AtlasHeight);  // bottom left
04850             //Vector2 uv1 = new Vector2(uv0.x, 1 - (m_cached_TextElement.y - padding - style_padding) / faceInfo.AtlasHeight);  // top left
04851             //Vector2 uv2 = new Vector2((m_cached_TextElement.x + padding + style_padding + m_cached_TextElement.width) / faceInfo.AtlasWidth, uv1.y); // top right
04852             //Vector2 uv3 = new Vector2(uv2.x, uv0.y); // bottom right
04853 
04854             // Store UV Information
04855             m_textInfo.characterInfo[m_characterCount].vertex_BL.uv = uv0;
04856             m_textInfo.characterInfo[m_characterCount].vertex_TL.uv = uv1;
04857             m_textInfo.characterInfo[m_characterCount].vertex_TR.uv = uv2;
04858             m_textInfo.characterInfo[m_characterCount].vertex_BR.uv = uv3;
04859             #endregion Setup UVs
04860 
04861 
04862             // Normal
04863             #region Setup Normals & Tangents
04864             //Vector3 normal = new Vector3(0, 0, -1);
04865             //m_textInfo.characterInfo[m_characterCount].vertex_BL.normal = normal;
04866             //m_textInfo.characterInfo[m_characterCount].vertex_TL.normal = normal;
04867             //m_textInfo.characterInfo[m_characterCount].vertex_TR.normal = normal;
04868             //m_textInfo.characterInfo[m_characterCount].vertex_BR.normal = normal;
04869 
04870             // Tangents
04871             //Vector4 tangent = new Vector4(-1, 0, 0, 1);
04872             //m_textInfo.characterInfo[m_characterCount].vertex_BL.tangent = tangent;
04873             //m_textInfo.characterInfo[m_characterCount].vertex_TL.tangent = tangent;
04874             //m_textInfo.characterInfo[m_characterCount].vertex_TR.tangent = tangent;
04875             //m_textInfo.characterInfo[m_characterCount].vertex_BR.tangent = tangent;
04876             #endregion end Normals & Tangents
04877         }
04878 
04879 
04886         protected virtual void SaveSpriteVertexInfo(Color32 vertexColor)
04887         {
04888             // Save the Vertex Position for the Character
04889             #region Setup Mesh Vertices
04890             m_textInfo.characterInfo[m_characterCount].vertex_BL.position = m_textInfo.characterInfo[m_characterCount].bottomLeft;
04891             m_textInfo.characterInfo[m_characterCount].vertex_TL.position = m_textInfo.characterInfo[m_characterCount].topLeft;
04892             m_textInfo.characterInfo[m_characterCount].vertex_TR.position = m_textInfo.characterInfo[m_characterCount].topRight;
04893             m_textInfo.characterInfo[m_characterCount].vertex_BR.position = m_textInfo.characterInfo[m_characterCount].bottomRight;
04894             #endregion
04895 
04896             // Vertex Color Alpha
04897             if (m_tintAllSprites) m_tintSprite = true;
04898             Color32 spriteColor = m_tintSprite ? m_spriteColor.Multiply(vertexColor) : m_spriteColor;
04899             spriteColor.a = spriteColor.a < m_fontColor32.a ? spriteColor.a = spriteColor.a < vertexColor.a ? spriteColor.a : vertexColor.a : m_fontColor32.a;
04900 
04901             Color32 c0 = spriteColor;
04902             Color32 c1 = spriteColor;
04903             Color32 c2 = spriteColor;
04904             Color32 c3 = spriteColor;
04905 
04906             if (m_enableVertexGradient)
04907             {
04908                 if (m_fontColorGradientPreset != null)
04909                 {
04910                     c0 = m_tintSprite ? c0.Multiply(m_fontColorGradientPreset.bottomLeft) : c0;
04911                     c1 = m_tintSprite ? c1.Multiply(m_fontColorGradientPreset.topLeft) : c1;
04912                     c2 = m_tintSprite ? c2.Multiply(m_fontColorGradientPreset.topRight) : c2;
04913                     c3 = m_tintSprite ? c3.Multiply(m_fontColorGradientPreset.bottomRight) : c3;
04914                 }
04915                 else
04916                 {
04917                     c0 = m_tintSprite ? c0.Multiply(m_fontColorGradient.bottomLeft) : c0;
04918                     c1 = m_tintSprite ? c1.Multiply(m_fontColorGradient.topLeft) : c1;
04919                     c2 = m_tintSprite ? c2.Multiply(m_fontColorGradient.topRight) : c2;
04920                     c3 = m_tintSprite ? c3.Multiply(m_fontColorGradient.bottomRight) : c3;
04921                 }
04922             }
04923 
04924             if (m_colorGradientPreset != null)
04925             {
04926                 c0 = m_tintSprite ? c0.Multiply(m_colorGradientPreset.bottomLeft) : c0;
04927                 c1 = m_tintSprite ? c1.Multiply(m_colorGradientPreset.topLeft) : c1;
04928                 c2 = m_tintSprite ? c2.Multiply(m_colorGradientPreset.topRight) : c2;
04929                 c3 = m_tintSprite ? c3.Multiply(m_colorGradientPreset.bottomRight) : c3;
04930             }
04931 
04932             m_textInfo.characterInfo[m_characterCount].vertex_BL.color = c0;
04933             m_textInfo.characterInfo[m_characterCount].vertex_TL.color = c1;
04934             m_textInfo.characterInfo[m_characterCount].vertex_TR.color = c2;
04935             m_textInfo.characterInfo[m_characterCount].vertex_BR.color = c3;
04936 
04937 
04938             // Setup UVs for the Character
04939             #region Setup UVs
04940             Vector2 uv0 = new Vector2(m_cached_TextElement.x / m_currentSpriteAsset.spriteSheet.width, m_cached_TextElement.y / m_currentSpriteAsset.spriteSheet.height);  // bottom left
04941             Vector2 uv1 = new Vector2(uv0.x, (m_cached_TextElement.y + m_cached_TextElement.height) / m_currentSpriteAsset.spriteSheet.height);  // top left
04942             Vector2 uv2 = new Vector2((m_cached_TextElement.x + m_cached_TextElement.width) / m_currentSpriteAsset.spriteSheet.width, uv1.y); // top right
04943             Vector2 uv3 = new Vector2(uv2.x, uv0.y); // bottom right
04944 
04945             // Store UV Information
04946             m_textInfo.characterInfo[m_characterCount].vertex_BL.uv = uv0;
04947             m_textInfo.characterInfo[m_characterCount].vertex_TL.uv = uv1;
04948             m_textInfo.characterInfo[m_characterCount].vertex_TR.uv = uv2;
04949             m_textInfo.characterInfo[m_characterCount].vertex_BR.uv = uv3;
04950             #endregion Setup UVs
04951 
04952 
04953             // Normal
04954             #region Setup Normals & Tangents
04955             //Vector3 normal = new Vector3(0, 0, -1);
04956             //m_textInfo.characterInfo[m_characterCount].vertex_BL.normal = normal;
04957             //m_textInfo.characterInfo[m_characterCount].vertex_TL.normal = normal;
04958             //m_textInfo.characterInfo[m_characterCount].vertex_TR.normal = normal;
04959             //m_textInfo.characterInfo[m_characterCount].vertex_BR.normal = normal;
04960 
04961             // Tangents
04962             //Vector4 tangent = new Vector4(-1, 0, 0, 1);
04963             //m_textInfo.characterInfo[m_characterCount].vertex_BL.tangent = tangent;
04964             //m_textInfo.characterInfo[m_characterCount].vertex_TL.tangent = tangent;
04965             //m_textInfo.characterInfo[m_characterCount].vertex_TR.tangent = tangent;
04966             //m_textInfo.characterInfo[m_characterCount].vertex_BR.tangent = tangent;
04967             #endregion end Normals & Tangents
04968 
04969         }
