05157 
05167         protected virtual void DrawUnderlineMesh(Vector3 start, Vector3 end, ref int index, float startScale, float endScale, float maxScale, float sdfScale, Color32 underlineColor)
05168         {
05169             if (m_cached_Underline_GlyphInfo == null)
05170             {
05171                 if (!TMP_Settings.warningsDisabled) Debug.LogWarning("Unable to add underline since the Font Asset doesn't contain the underline character.", this);
05172                 return;
05173             }
05174 
05175             int verticesCount = index + 12;
05176             // Check to make sure our current mesh buffer allocations can hold these new Quads.
05177             if (verticesCount > m_textInfo.meshInfo[0].vertices.Length)
05178             {
05179                 // Resize Mesh Buffers
05180                 m_textInfo.meshInfo[0].ResizeMeshInfo(verticesCount / 4);
05181             }
05182 
05183             // Adjust the position of the underline based on the lowest character. This matters for subscript character.
05184             start.y = Mathf.Min(start.y, end.y);
05185             end.y = Mathf.Min(start.y, end.y);
05186 
05187             float segmentWidth = m_cached_Underline_GlyphInfo.width / 2 * maxScale;
05188 
05189             if (end.x - start.x < m_cached_Underline_GlyphInfo.width * maxScale)
05190             {
05191                 segmentWidth = (end.x - start.x) / 2f;
05192             }
05193 
05194             float startPadding = m_padding * startScale / maxScale;
05195             float endPadding = m_padding * endScale / maxScale;
05196 
05197             float underlineThickness = m_cached_Underline_GlyphInfo.height;
05198 
05199             // UNDERLINE VERTICES FOR (3) LINE SEGMENTS
05200             #region UNDERLINE VERTICES
05201             Vector3[] vertices = m_textInfo.meshInfo[0].vertices;
05202 
05203             // Front Part of the Underline
05204             vertices[index + 0] = start + new Vector3(0, 0 - (underlineThickness + m_padding) * maxScale, 0); // BL
05205             vertices[index + 1] = start + new Vector3(0, m_padding * maxScale, 0); // TL
05206             vertices[index + 2] = vertices[index + 1] + new Vector3(segmentWidth, 0, 0); // TR
05207             vertices[index + 3] = vertices[index + 0] + new Vector3(segmentWidth, 0, 0); // BR
05208 
05209             // Middle Part of the Underline
05210             vertices[index + 4] = vertices[index + 3]; // BL
05211             vertices[index + 5] = vertices[index + 2]; // TL
05212             vertices[index + 6] = end + new Vector3(-segmentWidth, m_padding * maxScale, 0);  // TR
05213             vertices[index + 7] = end + new Vector3(-segmentWidth, -(underlineThickness + m_padding) * maxScale, 0); // BR
05214 
05215             // End Part of the Underline
05216             vertices[index + 8] = vertices[index + 7]; // BL
05217             vertices[index + 9] = vertices[index + 6]; // TL
05218             vertices[index + 10] = end + new Vector3(0, m_padding * maxScale, 0); // TR
05219             vertices[index + 11] = end + new Vector3(0, -(underlineThickness + m_padding) * maxScale, 0); // BR
05220             #endregion
05221 
05222             // UNDERLINE UV0
05223             #region HANDLE UV0
05224             Vector2[] uvs0 = m_textInfo.meshInfo[0].uvs0;
05225 
05226             // Calculate UV required to setup the 3 Quads for the Underline.
05227             Vector2 uv0 = new Vector2((m_cached_Underline_GlyphInfo.x - startPadding) / m_fontAsset.fontInfo.AtlasWidth, 1 - (m_cached_Underline_GlyphInfo.y + m_padding + m_cached_Underline_GlyphInfo.height) / m_fontAsset.fontInfo.AtlasHeight);  // bottom left
05228             Vector2 uv1 = new Vector2(uv0.x, 1 - (m_cached_Underline_GlyphInfo.y - m_padding) / m_fontAsset.fontInfo.AtlasHeight);  // top left
05229             Vector2 uv2 = new Vector2((m_cached_Underline_GlyphInfo.x - startPadding + m_cached_Underline_GlyphInfo.width / 2) / m_fontAsset.fontInfo.AtlasWidth, uv1.y); // Mid Top Left
05230             Vector2 uv3 = new Vector2(uv2.x, uv0.y); // Mid Bottom Left
05231             Vector2 uv4 = new Vector2((m_cached_Underline_GlyphInfo.x + endPadding + m_cached_Underline_GlyphInfo.width / 2) / m_fontAsset.fontInfo.AtlasWidth, uv1.y); // Mid Top Right
05232             Vector2 uv5 = new Vector2(uv4.x, uv0.y); // Mid Bottom right
05233             Vector2 uv6 = new Vector2((m_cached_Underline_GlyphInfo.x + endPadding + m_cached_Underline_GlyphInfo.width) / m_fontAsset.fontInfo.AtlasWidth, uv1.y); // End Part - Bottom Right
05234             Vector2 uv7 = new Vector2(uv6.x, uv0.y); // End Part - Top Right
05235 
05236             // Left Part of the Underline
05237             uvs0[0 + index] = uv0; // BL
05238             uvs0[1 + index] = uv1; // TL
05239             uvs0[2 + index] = uv2; // TR
05240             uvs0[3 + index] = uv3; // BR
05241 
05242             // Middle Part of the Underline
05243             uvs0[4 + index] = new Vector2(uv2.x - uv2.x * 0.001f, uv0.y);
05244             uvs0[5 + index] = new Vector2(uv2.x - uv2.x * 0.001f, uv1.y);
05245             uvs0[6 + index] = new Vector2(uv2.x + uv2.x * 0.001f, uv1.y);
05246             uvs0[7 + index] = new Vector2(uv2.x + uv2.x * 0.001f, uv0.y);
05247 
05248             // Right Part of the Underline
05249             uvs0[8 + index] = uv5;
05250             uvs0[9 + index] = uv4;
05251             uvs0[10 + index] = uv6;
05252             uvs0[11 + index] = uv7;
05253             #endregion
05254 
05255             // UNDERLINE UV2
05256             #region HANDLE UV2 - SDF SCALE
05257             // UV1 contains Face / Border UV layout.
05258             float min_UvX = 0;
05259             float max_UvX = (vertices[index + 2].x - start.x) / (end.x - start.x);
05260 
05261             //Calculate the xScale or how much the UV's are getting stretched on the X axis for the middle section of the underline.
05262             float xScale = Mathf.Abs(sdfScale);
05263 
05264             Vector2[] uvs2 = m_textInfo.meshInfo[0].uvs2;
05265 
05266             uvs2[0 + index] = PackUV(0, 0, xScale);
05267             uvs2[1 + index] = PackUV(0, 1, xScale);
05268             uvs2[2 + index] = PackUV(max_UvX, 1, xScale);
05269             uvs2[3 + index] = PackUV(max_UvX, 0, xScale);
05270 
05271             min_UvX = (vertices[index + 4].x - start.x) / (end.x - start.x);
05272             max_UvX = (vertices[index + 6].x - start.x) / (end.x - start.x);
05273 
05274             uvs2[4 + index] = PackUV(min_UvX, 0, xScale);
05275             uvs2[5 + index] = PackUV(min_UvX, 1, xScale);
05276             uvs2[6 + index] = PackUV(max_UvX, 1, xScale);
05277             uvs2[7 + index] = PackUV(max_UvX, 0, xScale);
05278 
05279             min_UvX = (vertices[index + 8].x - start.x) / (end.x - start.x);
05280             max_UvX = (vertices[index + 6].x - start.x) / (end.x - start.x);
05281 
05282             uvs2[8 + index] = PackUV(min_UvX, 0, xScale);
05283             uvs2[9 + index] = PackUV(min_UvX, 1, xScale);
05284             uvs2[10 + index] = PackUV(1, 1, xScale);
05285             uvs2[11 + index] = PackUV(1, 0, xScale);
05286             #endregion
05287 
05288             // UNDERLINE VERTEX COLORS
05289             #region
05290             // Alpha is the lower of the vertex color or tag color alpha used.
05291             underlineColor.a = m_fontColor32.a < underlineColor.a ? (byte)(m_fontColor32.a) : (byte)(underlineColor.a);
05292 
05293             Color32[] colors32 = m_textInfo.meshInfo[0].colors32;
05294             colors32[0 + index] = underlineColor;
05295             colors32[1 + index] = underlineColor;
05296             colors32[2 + index] = underlineColor;
05297             colors32[3 + index] = underlineColor;
05298 
05299             colors32[4 + index] = underlineColor;
05300             colors32[5 + index] = underlineColor;
05301             colors32[6 + index] = underlineColor;
05302             colors32[7 + index] = underlineColor;
05303 
05304             colors32[8 + index] = underlineColor;
05305             colors32[9 + index] = underlineColor;
05306             colors32[10 + index] = underlineColor;
05307             colors32[11 + index] = underlineColor;
05308             #endregion
05309 
05310             index += 12;
05311         }
05312 
