05314         protected virtual void DrawTextHighlight(Vector3 start, Vector3 end, ref int index, Color32 highlightColor)
05315         {
05316             if (m_cached_Underline_GlyphInfo == null)
05317             {
05318                 if (!TMP_Settings.warningsDisabled) Debug.LogWarning("Unable to add underline since the Font Asset doesn't contain the underline character.", this);
05319                 return;
05320             }
05321 
05322             int verticesCount = index + 4;
05323             // Check to make sure our current mesh buffer allocations can hold these new Quads.
05324             if (verticesCount > m_textInfo.meshInfo[0].vertices.Length)
05325             {
05326                 // Resize Mesh Buffers
05327                 m_textInfo.meshInfo[0].ResizeMeshInfo(verticesCount / 4);
05328             }
05329 
05330             // UNDERLINE VERTICES FOR (3) LINE SEGMENTS
05331             #region HIGHLIGHT VERTICES
05332             Vector3[] vertices = m_textInfo.meshInfo[0].vertices;
05333 
05334             // Front Part of the Underline
05335             vertices[index + 0] = start; // BL
05336             vertices[index + 1] = new Vector3(start.x, end.y, 0); // TL
05337             vertices[index + 2] = end; // TR
05338             vertices[index + 3] = new Vector3(end.x, start.y, 0); // BR
05339             #endregion
05340 
05341             // UNDERLINE UV0
05342             #region HANDLE UV0
05343             Vector2[] uvs0 = m_textInfo.meshInfo[0].uvs0;
05344 
05345             // Calculate UV required to setup the 3 Quads for the Underline.
05346             Vector2 uv0 = new Vector2((m_cached_Underline_GlyphInfo.x + m_cached_Underline_GlyphInfo.width / 2) / m_fontAsset.fontInfo.AtlasWidth, 1 - (m_cached_Underline_GlyphInfo.y + m_cached_Underline_GlyphInfo.height / 2) / m_fontAsset.fontInfo.AtlasHeight);  // bottom left
05347             //Vector2 uv1 = new Vector2(uv0.x, uv0.y);  // top left
05348             //Vector2 uv2 = new Vector2(uv0.x, uv0.y); // Top Right
05349             //Vector2 uv3 = new Vector2(uv2.x, uv0.y); // Bottom Right
05350 
05351             // Left Part of the Underline
05352             uvs0[0 + index] = uv0; // BL
05353             uvs0[1 + index] = uv0; // TL
05354             uvs0[2 + index] = uv0; // TR
05355             uvs0[3 + index] = uv0; // BR
05356             #endregion
05357 
05358             // UNDERLINE UV2
05359             #region HANDLE UV2 - SDF SCALE
05360             // UV1 contains Face / Border UV layout.
05361             //float min_UvX = 0;
05362             //float max_UvX = (vertices[index + 2].x - start.x) / (end.x - start.x);
05363 
05365             //float xScale = 0; // Mathf.Abs(sdfScale);
05366 
05367             Vector2[] uvs2 = m_textInfo.meshInfo[0].uvs2;
05368             Vector2 customUV = new Vector2(0, 1);
05369             uvs2[0 + index] = customUV; // PackUV(-0.2f, -0.2f, xScale);
05370             uvs2[1 + index] = customUV; // PackUV(-0.2f, -0.1f, xScale);
05371             uvs2[2 + index] = customUV; // PackUV(-0.1f, -0.1f, xScale);
05372             uvs2[3 + index] = customUV; // PackUV(-0.1f, -0.2f, xScale);
05373             #endregion
05374 
05375             // HIGHLIGHT VERTEX COLORS
05376             #region
05377             // Alpha is the lower of the vertex color or tag color alpha used.
05378             highlightColor.a = m_fontColor32.a < highlightColor.a ? m_fontColor32.a : highlightColor.a;
05379 
05380             Color32[] colors32 = m_textInfo.meshInfo[0].colors32;
05381             colors32[0 + index] = highlightColor;
05382             colors32[1 + index] = highlightColor;
05383             colors32[2 + index] = highlightColor;
05384             colors32[3 + index] = highlightColor;
05385             #endregion
05386 
05387             index += 4;
05388         }
