05390 
05394         protected void LoadDefaultSettings()
05395         {
05396             if (m_text == null || m_isWaitingOnResourceLoad)
05397             {
05398                 if (TMP_Settings.autoSizeTextContainer)
05399                     autoSizeTextContainer = true;
05400                 else
05401                 {
05402                     m_rectTransform = this.rectTransform;
05403 
05404                     if (GetType() == typeof(TextMeshPro))
05405                         m_rectTransform.sizeDelta = TMP_Settings.defaultTextMeshProTextContainerSize;
05406                     else
05407                         m_rectTransform.sizeDelta = TMP_Settings.defaultTextMeshProUITextContainerSize;
05408                 }
05409 
05410                 m_enableWordWrapping = TMP_Settings.enableWordWrapping;
05411                 m_enableKerning = TMP_Settings.enableKerning;
05412                 m_enableExtraPadding = TMP_Settings.enableExtraPadding;
05413                 m_tintAllSprites = TMP_Settings.enableTintAllSprites;
05414                 m_parseCtrlCharacters = TMP_Settings.enableParseEscapeCharacters;
05415                 m_fontSize = m_fontSizeBase = TMP_Settings.defaultFontSize;
05416                 m_fontSizeMin = m_fontSize * TMP_Settings.defaultTextAutoSizingMinRatio;
05417                 m_fontSizeMax = m_fontSize * TMP_Settings.defaultTextAutoSizingMaxRatio;
05418                 m_isAlignmentEnumConverted = true;
05419                 m_isWaitingOnResourceLoad = false;
05420             }
05421             else if (m_isAlignmentEnumConverted == false)
05422             {
05423                 // Convert TextAlignmentOptions enumerations.
05424                 m_isAlignmentEnumConverted = true;
05425                 m_textAlignment = TMP_Compatibility.ConvertTextAlignmentEnumValues(m_textAlignment);
05426             }
05427         }
