05459         protected void ReplaceTagWithCharacter(int[] chars, int insertionIndex, int tagLength, char c)
05460         {
05461             chars[insertionIndex] = c;
05462 
05463             for (int i = insertionIndex + tagLength; i < chars.Length; i++)
05464             {
05465                 chars[i - 3] = chars[i];
05466             }
05467         }
