05483 
05488         protected TMP_FontAsset GetFontAssetForWeight(int fontWeight)
05489         {
05490             bool isItalic = (m_style & FontStyles.Italic) == FontStyles.Italic || (m_fontStyle & FontStyles.Italic) == FontStyles.Italic;
05491 
05492             TMP_FontAsset fontAsset = null;
05493 
05494             int weightIndex = fontWeight / 100;
05495 
05496             if (isItalic)
05497                 fontAsset = m_currentFontAsset.fontWeights[weightIndex].italicTypeface;
05498             else
05499                 fontAsset = m_currentFontAsset.fontWeights[weightIndex].regularTypeface;
05500 
05501             return fontAsset;
05502         }
