05534         public virtual string GetParsedText()
05535         {
05536             if (m_textInfo == null)
05537                 return string.Empty;
05538 
05539             int characterCount = m_textInfo.characterCount;
05540 
05541             // TODO - Could implement some static buffer pool shared by all instances of TMP objects.
05542             char[] buffer = new char[characterCount];
05543 
05544             for (int i = 0; i < characterCount && i < m_textInfo.characterInfo.Length; i++)
05545             {
05546                 buffer[i] = m_textInfo.characterInfo[i].character;
05547             }
05548 
05549             return new string(buffer);
05550         }
