05572 
05580         protected Vector2 PackUV(float x, float y, float scale)
05581         {
05582             Vector2 output;
05583 
05584             output.x = (int)(x * 511);
05585             output.y = (int)(y * 511);
05586 
05587             output.x = (output.x * 4096) + output.y;
05588             output.y = scale;
05589 
05590             return output;
05591         }
05592 
05593 
05600         protected float PackUV(float x, float y)
05601         {
05602             double x0 = (int)(x * 511);
05603             double y0 = (int)(y * 511);
05604 
05605             return (float)((x0 * 4096) + y0);
05606         }
