05647         protected int HexToInt(char hex)
05648         {
05649             switch (hex)
05650             {
05651                 case '0': return 0;
05652                 case '1': return 1;
05653                 case '2': return 2;
05654                 case '3': return 3;
05655                 case '4': return 4;
05656                 case '5': return 5;
05657                 case '6': return 6;
05658                 case '7': return 7;
05659                 case '8': return 8;
05660                 case '9': return 9;
05661                 case 'A': return 10;
05662                 case 'B': return 11;
05663                 case 'C': return 12;
05664                 case 'D': return 13;
05665                 case 'E': return 14;
05666                 case 'F': return 15;
05667                 case 'a': return 10;
05668                 case 'b': return 11;
05669                 case 'c': return 12;
05670                 case 'd': return 13;
05671                 case 'e': return 14;
05672                 case 'f': return 15;
05673             }
05674             return 15;
05675         }
